/*!40014 SET @OLD_UNIQUE_CHECKS=@@UNIQUE_CHECKS, UNIQUE_CHECKS=0 */;
/*!40014 SET @OLD_FOREIGN_KEY_CHECKS=@@FOREIGN_KEY_CHECKS, FOREIGN_KEY_CHECKS=0 */;
/*!40101 SET @OLD_SQL_MODE=@@SQL_MODE, SQL_MODE='NO_AUTO_VALUE_ON_ZERO' */;
/*!40111 SET @OLD_SQL_NOTES=@@SQL_NOTES, SQL_NOTES=0 */;
CREATE DATABASE /*!32312 IF NOT EXISTS*/`icca_razum` /*!40100 DEFAULT CHARACTER SET utf8 */;

USE `icca_razum`;

/*Table structure for table `articles` */

DROP TABLE IF EXISTS `articles`;

CREATE TABLE `articles` (
  `ArticleId` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `LangId` char(2) NOT NULL,
  `ArticleCreateTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `ArticleModifyTime` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',
  `ArticleAuthor` int(11) NOT NULL,
  `ArticleActivated` tinyint(4) NOT NULL DEFAULT '0',
  `ArticleOptions` int(10) unsigned NOT NULL DEFAULT '7' COMMENT 'bitmask\n1 - show author\n2 - show create time\n4 - show modified time',
  `ArticleValidFrom` timestamp NULL DEFAULT NULL,
  `ArticleValidTo` timestamp NULL DEFAULT NULL,
  `ArticleTitle` text,
  `ArticleSubtitle` text,
  `ArticleContent` text,
  `ArticleTags` text,
  `ArticleMetaKeywords` text,
  `ArticleMetaDescription` text,
  PRIMARY KEY (`ArticleId`),
  KEY `ArticleAuthor` (`ArticleAuthor`),
  KEY `LangId` (`LangId`),
  CONSTRAINT `articles_ibfk_1` FOREIGN KEY (`ArticleAuthor`) REFERENCES `users` (`UserId`),
  CONSTRAINT `articles_ibfk_2` FOREIGN KEY (`LangId`) REFERENCES `languages` (`LangId`)
) ENGINE=InnoDB AUTO_INCREMENT=83 DEFAULT CHARSET=utf8;

/*Data for the table `articles` */

insert  into `articles`(`ArticleId`,`LangId`,`ArticleCreateTime`,`ArticleModifyTime`,`ArticleAuthor`,`ArticleActivated`,`ArticleOptions`,`ArticleValidFrom`,`ArticleValidTo`,`ArticleTitle`,`ArticleSubtitle`,`ArticleContent`,`ArticleTags`,`ArticleMetaKeywords`,`ArticleMetaDescription`) values (1,'bg','2013-03-04 14:14:41','2013-05-10 15:12:12',1,1,7,NULL,NULL,'Народно читалище “Разум 1883”','','<h3>Кои сме ние?</h3>\r\n<p>Народно читалище \"Разум 1883\" – Монтана  е основано  през 1883г.\r\nВековната му история разказва за богат, разнообразен и интересен  културен живот,\r\nпревърнал го в истинско средище за просвета и култура.</p>\r\n<p>Ползвайки авторитета на най-стария културен институт и  здраво стъпило на демократичната читалищна традиция, днес  читалище \"Разум 1883\"  изгражда своя съвременен образ, адекватен на днешния ден и  отговорен към предизвикателствата на утрешния.</p>\r\n\r\n<h3>Нашата мисия</h3>\r\n<p>Читалище \"Разум 1883\" е традиционна културно-просветна структура призвана да развива и обогатява духовния живот на  местната общност, да предоставя среда за общуване, обмен на информация, знания  и възможност за развитие на самобитни таланти от всички поколения и възрасти.</p>\r\n','','',''),(2,'bg','2013-03-04 14:15:18','2013-03-04 14:15:18',1,1,7,NULL,NULL,'Цифри и факти','','<dl>\r\n	<dt>1883 г</dt>\r\n	<dd>Основано  читалище \"Разум\" – Монтана,  обявено в \"Училищен алманах\" на Максимов – по данни на Министерството на просвещението,  784, т 77.</dd>\r\n\r\n	<dt>1894 г</dt>\r\n	<dd>Възобновена дейността на читалище \"Разум\".<br/>\r\n	Председател на Настоятелството – Гаврил Душков –от 1894 до 1912 г.<br/>\r\n	За седалище на читалището се определя училище \"Кирил и Методий\" Кафене \"Шипка\" / Качовото кафене.</dd>\r\n\r\n	<dt>1894 г</dt>\r\n	<dd>Начало на библиотечната дейност на читалището – 200 подарени книги, съхранявани в кафене \"Шипка\".</dd>\r\n\r\n	<dt>1911 г</dt>\r\n	<dd>Закупен парцел от Настоятелството на ч-ще \"Разум\" /бивш турски конак / чрез адвоката Цоло Мислов, публикувано в ДВ бр 57 през 1911 година.</dd>\r\n\r\n	<dt>1912 г</dt>\r\n	<dd>Читалище \"Разум\" влиза в Съюза на народните читалища.</dd>\r\n\r\n	<dt>1912-1918 г</dt>\r\n	<dd>Театралният състав на читалището участва н.а. Стефан Савов. Като актьор в Народния театър поставя пиеси на читалищната сцена.</dd>\r\n\r\n	<dt>до 1920 г</dt>\r\n	<dd>Набирани средства за нова читалищна сграда в Българска Земеделска Банка по време на войните.</dd>\r\n\r\n	<dt>1920 г</dt>\r\n	<dd>Създаден граждански хор от Иван Бърдаров от 80 изпълнители.</dd>\r\n\r\n	<dt>1920-1934 г</dt>\r\n	<dd>Старата сграда се преустроява – ремонт за 322 000 лв.</dd>\r\n\r\n	<dt>1922 г</dt>\r\n	<dd>Учителят Иван Бърдаров прави първата оперетна постановка – \"Самодивското изворче\".</dd>\r\n\r\n	<dt>1926 г</dt>\r\n	<dd>Читалището организира детска забавачница с 30 деца.</dd>\r\n\r\n	<dt>1926 г</dt>\r\n	<dd>Създаден симфоничен оркестър</dd>\r\n\r\n	<dt>1926 г</dt>\r\n	<dd>Обновен театрален състав.<br/>\r\n	Открива се народен университет – първа лекция „Ролята на читалището\".</dd>\r\n\r\n	<dt>1927 г</dt>\r\n	<dd>В читалището се прожектира първият филм \"Тайнственият капитан\", озвучаван на живо от симфоничния оркестър.</dd>\r\n\r\n	<dt>1929 г</dt>\r\n	<dd>Създава се духов оркестър.</dd>\r\n\r\n	<dt>1930 г</dt>\r\n	<dd>Открива се лятно кино с филма „Волга, Волга\".</dd>\r\n\r\n	<dt>1934 г</dt>\r\n	<dd>Решение на настоятелството за построяване на нова читалищна сграда,<br/>\r\n	сформира се строителен комитет, надзорник - Владимир Попов – адвокат.<br/>\r\n	С лични средства на настоятелството и контролната комисия / 1200 лв /се закупуват 200 кубика камъни за основите на сградата.<br/>\r\n	Настоятелството отправя апел към гражданството за помощи.</dd>\r\n\r\n	<dt>20.VІ.1934 г</dt>\r\n	<dd>Полага се основния камък на бъдещата сграда с водосвет.<br/>\r\n	В основния камък на сградата се зазижда послание:<br/>\r\n	\"Днес, сряда, третия ден от седмицата, двадесети ден от месец юни 1934<br/>\r\n	година се положи този основен камък за построяване сграда на читалище под име \"Разум\" в гр Фердинанд.<br/>\r\n	Строежът се извърши от дарения, дадени от гражданите на града и при проект, съставен от техника Асен Иванчев.<br/>\r\n	Призоваваме божията милост и благодат за благоденствието на читалището!\"</dd>\r\n\r\n	<dt>8.ІХ.1934 г</dt>\r\n	<dd>Настоятелството взема решение да сключи заем с РКБ за 50 000 лв, който се отпуска същия месец</dd>\r\n\r\n	<dt>1935 г</dt>\r\n	<dd>Строителството на читалищната сграда стига до първа плоча.</dd>\r\n\r\n	<dt>1936 г</dt>\r\n	<dd>Завършва първия етап от строежа на сградата- поставя се покривна конструкция с помощта на заеми и дарения от Колоездачно дружество, търговско сдружение в града, църковното настоятелство, туристическо дружество.</dd>\r\n\r\n	<dt>26.ХІІ.1937 г</dt>\r\n	<dd>Тържествено се открива читалищния салон.</dd>\r\n\r\n	<dt>1938 г</dt>\r\n	<dd>Читалището влиза в полуготова сграда с почти нищожно обзавеждане.<br/>\r\n	Районната банка дава още 40 000 лв за оборудване, като се подписва договор между Настоятелството и банката за даване киното и салона под наем, като приходите се ползват от Банката и така се връщат заемите.</dd>\r\n\r\n	<dt>24.V.1938 г</dt>\r\n	<dd>Открива се библиотека и читалня в новата сграда</dd>\r\n\r\n	<dt>1943-1944 г</dt>\r\n	<dd>В град Фердинанд е евакуирана Софийската народна опера, която на сцената на читалището представя 5 пъти оперета \"Гергана\" на Маестро Атанасов</dd>\r\n\r\n	<dt>1944 г</dt>\r\n	<dd>Оперетна постановка \"Бойчо\" – диригент на хора и актьор - Самуел Видас</dd>\r\n\r\n	<dt>1945 г</dt>\r\n	<dd>Оперетна постановка \"Синята скала\"</dd>\r\n\r\n	<dt>1946 г</dt>\r\n	<dd>Градският хор и симфоничният оркестър създават ансамбъл – един от най-популярните и успешни състави на читалището</dd>\r\n\r\n	<dt>1950 г</dt>\r\n	<dd>Създава се танцов състав с ръководител Милотинка Антонова</dd>\r\n\r\n	<dt>1951 г</dt>\r\n	<dd>Създава се литературен кръжок „Огоста\"</dd>\r\n\r\n	<dt>1952 г</dt>\r\n	<dd>Читалищното кино става държавно и се отделя от читалището</dd>\r\n\r\n	<dt>1955 г</dt>\r\n	<dd>Създава се детска музикална школа – директор Апостол Кръстев</dd>\r\n	<dd>Към читалището се създава цигански ансамбъл с ръководител Константин Михайлов - Фицо</dd>\r\n\r\n	<dt>1956 г</dt>\r\n	<dd>Създава се народен хор с диригент Славчо Наков</dd>\r\n	<dd>Създава се детски отдел към библиотеката</dd>\r\n	<dd>Окръжен преглед на художествената самодейност в град Враца – І–во място за Ансамбъла / симфоничен оркестър и градски хор /</dd>\r\n	<dd>Оперетна постановка \"Майска нощ\"</dd>\r\n\r\n	<dt>1958 г</dt>\r\n	<dd>Окръжен преглед за духови оркестри – І място за оркестъра при читалището</dd>\r\n\r\n	<dt>1959 г</dt>\r\n	<dd>Библиотеката става Окръжна методическа</dd>\r\n	<dd>Създава се народен оркестър с ръководител Петър Георгиев</dd>\r\n	<dd>Народният хор, танцовият състав и народният оркестър се обединяват в ансамбъл, правят се постановките \"Защо малините са кървавочервени\", \"Хорото на нехаевци\", \"Сватбата на Ружка\" и др</dd>\r\n\r\n	<dt>1962 г</dt>\r\n	<dd>Създава се балетна школа с ръководител Роза Будакова</dd>\r\n\r\n	<dt>1964 г</dt>\r\n	<dd>Втори РФХС – златен медал за оперетата \"Целувката на Чанита\", златни и за Павлина Папуркова и Димитър Найденов, Златен медал и лауреатско звание за народния ансамбъл</dd>\r\n\r\n	<dt>1965 г</dt>\r\n	<dd>Премиера на оперетата „Прилепът\"</dd>\r\n\r\n	<dt>1966 г</dt>\r\n	<dd>Създава се фото кръжок с ръководител Христо Кузмов</dd>\r\n\r\n	<dt>1967 г</dt>\r\n	<dd>Трети РФХС – златен медал за танцовия състав, духовия оркестър и народния хор</dd>\r\n\r\n	<dt>1968 г</dt>\r\n	<dd>Създава се куклен театър с ръководител Петрана Стоянова, първа постановка пиесата – „Таралеж –съдия\"</dd>\r\n\r\n	<dt>1969 г</dt>\r\n	<dd>Премиера на оперетата Бедният студент\" – зл медал от ІІІ РФХС</dd>\r\n\r\n	<dt>1.9.1969 г</dt>\r\n	<dd>Читалищният театър става Държавен драматичен театър</dd>\r\n\r\n	<dt>1973 г</dt>\r\n	<dd>Създава се детски оперетен колектив – първа постановка \"Приказен сън\" с диригент Александър Чакалов</dd>\r\n	<dd>Читалището е наградено с Орден \"Кирил и Методий\"</dd>\r\n	<dd>Премиера на оперетата „Волният вятър\"</dd>\r\n	<dd>Създава се симфоничен оркестър под диригентството на Александър Чакалов и Детски акордеонен оркестър – р-л Лило Лилов</dd>\r\n	<dd>Детска оперетна постановка \"Приказен сън\"</dd>\r\n\r\n	<dt>1974 г</dt>\r\n	<dd>Детска оперета \"Счупената чаша\"- премиера</dd>\r\n	<dd>Премиера на оперетата „Веселата вдовица\".</dd>\r\n	<dd>Отново се създава симфоничен оркестър – диригент Петър Ганев</dd>\r\n	<dd>Четвърти Републикански фестивал на художествената самодейност – златни медали за танцов състав, духов оркестър, народен хор, детска оперета и оперетен колектив</dd>\r\n\r\n	<dt>1976 г</dt>\r\n	<dd>Премиера на оперетата „Хубавата Елена„</dd>\r\n\r\n	<dt>1977 г</dt>\r\n	<dd>Премиера на оперетата „Птицепродавецът\"</dd>\r\n\r\n	<dt>1979 г</dt>\r\n	<dd>Създава се Школа по изкуствата на базата на музикалната школа, балетната школа, открива се школа по изобразително изкуство, към школата се прехвърля детски танцов състав, състава за художествено слово</dd>\r\n	<dd>Втора постановка на оперетата „Българи от старо време\"</dd>\r\n	<dd>Оперетният театър гостува на сцената на ДМТ „Ст Македонски\" - София с оперетата „Българи от старо време\"</dd>\r\n	<dd>50 годишен юбилей на духов оркестър – дирижира Дико Илиев</dd>\r\n	<dd>Пети РФХС – Лауреатско звание и златни медали за духов оркестър, народен хор, оперетен колектив, танцов състав, детско-юношески хор</dd>\r\n\r\n	<dt>1980 г</dt>\r\n	<dd>25 г от създаването на народния хор – орден „Кирил и Методий\" – ІІ ст</dd>\r\n	<dd>Премиера на оперетата „Службогонци\" , режисьор Емил Пенчев</dd>\r\n	<dd>Първи преглед на детските музикални школи от Северозападна България под домакинството на ОШИ „Д. Христов\"</dd>\r\n	<dd>Създаден литературен кръжок „Единство, творчество, красота\" с р-л Любка Захариева</dd>\r\n	<dd>Цикъл от ежемесечни изяви „Естетическа панорама\" с участието на Чавдар Маждраков, Таня Христова, Агапия баларева, Кеворк Кеворкян и хор „Йоан Кукузел\"</dd>\r\n	<dd>„Естетическа панорама „ – ІІ тема – „Песните на българина\" с участието на Николай Кауфман, Иван вълев, Михайл Букурушлиев</dd>\r\n	<dd>Създава се Градски смесен хор – диригент Чавдар Маждраков</dd>\r\n\r\n	<dt>1981 г</dt>\r\n	<dd>25 годишен юбилей на Народния хор - награден с орден \"Кирил и Методий\" </dd>\r\n	<dd>Премиера на оперетата Графиня Марица\" от Калман, режисьор Емил Пенчев</dd>\r\n\r\n	<dt>1982 г</dt>\r\n	<dd>Премиера на оперетата „Лелята на Чарли\", режисьор Динко Чомаков</dd>\r\n	<dd>Община Монтана и читалище \"Разум\" домакини на Първите прегледи на духовите оркестри \"Дико Илиев\"</dd>\r\n\r\n	<dt>1983 г</dt>\r\n	<dd>Танцовият състав и народния хор създават ансамбъл за народни песни и танци с ръководител Любомир Гуглин</dd>\r\n	<dd>Създава се клавирно трио, вокален квартет</dd>\r\n	<dd>Към читалището съществуват следните състави - школа по изкуствата, куклен театър, градски смесен хор, танцов състав, народен хор, детски танцов състав, духов оркестър, клавирно трио, мажоретен състав, оперетен състав, джаз балет, студио за художествено слово, вокален квартет</dd>\r\n	<dd>Отбелязва се 100 годишнината на читалище „Разум\", жертвеният огън се запалва от Дико Илиев</dd>\r\n\r\n	<dt>1985 г</dt>\r\n	<dd>концерт на вокален квартет и солисти от АНПТ в гр Пирот Югославия</dd>\r\n\r\n	<dt>1986 г</dt>\r\n	<dd>Среща с композитора Борис Карадимчев и групата „Пим-пам\"</dd>\r\n\r\n	<dt>1988 г</dt>\r\n	<dd>разменно гостуване на танцовия състав и състав от гр Янина , Гърция</dd>\r\n\r\n	<dt>1989 г</dt>\r\n	<dd>Участие на народния оркестър, танцовия състав и солисти в дните на България в Западен Берлин</dd>\r\n	<dd>Създава се група за стари песни и шлагери \"Носталгия\"</dd>\r\n	<dd>Участие на самодейни състави на читалището в дните на България в Берлин</dd>\r\n\r\n	<dt>1990 г</dt>\r\n	<dd>Оперетна постановка \"Моята прекрасна лейди\" – режисьор Лиляна Кисьова</dd>\r\n\r\n	<dt>1991 г</dt>\r\n	<dd> В читалището идва мандолинен оркестър с диригент Любомир Калчев</dd>\r\n\r\n	<dt>1992 г</dt>\r\n	<dd>Създава се детска духова школа</dd>\r\n	<dd>\"Оперетно вариете\" – вечер, посветена на първата оперетна постановка в Монтана</dd>\r\n	<dd>На читалището гостува хор \"Йоан Кукузел\" по повод Денят на будителите</dd>\r\n	<dd>Гостува студентска телевизионна програма \"КУ – КУ\"</dd>\r\n	<dd>Авторски рецитал \"Остаряваме бавно\" на Недялко Йорданов и Хайгашот Агасян</dd>\r\n	<dd>Духовият оркестър е поканен за участие в новогодишен концерт по БНТ</dd>\r\n\r\n	<dt>1993 г</dt>\r\n	<dd>Шоу-вечер с участието на Иван Кулеков и Христо Бойчев</dd>\r\n	<dd>Тържества по повод 110 години от създаването на Читалище \"Разум\"</dd>\r\n	<dd>Концерт на трио \"Българка\" и Теодоси Спасов</dd>\r\n	<dd>Запис за телевизията и участие в концерт \"Ехо от \"Златен кестен\" на група \"Носталгия\"</dd>\r\n\r\n	<dt>1994 г</dt>\r\n	<dd>Създава се народен оркестър с ръководител Петър Каменов</dd>\r\n	<dd>Втора награда за група \"Носталгия\" на \"Златен кестен\" - Петрич</dd>\r\n	<dd>Концерт на група \"Носталгия\" в НДК</dd>\r\n\r\n	<dt>1995 г</dt>\r\n	<dd>Авторски рецитал на поета Стефан Цанев и Доротея Тончева</dd>\r\n	<dd>Вечер на старата градска песен – гост и водещ Драган Тенев – </dd>\r\n	<dd>Концерт на група \"Носталгия\" в гр Гостивар, Р Македония</dd>\r\n	<dd>Първа награда от фестивала \"Дионисополис\" – гр Балчик за група \"Носталгия\"</dd>\r\n	<dd>Участие на народния хор в Петропавловския събор - надпяване</dd>\r\n	<dd>Създава се клавирно трио \"Монтана\"</dd>\r\n	<dd>Поетична вечер с участието на Дамян Дамянов и Надежда Захариева</dd>\r\n\r\n	<dt>1996 г</dt>\r\n	<dd>Тържество по повод 40 години от създаването на народния хор</dd>\r\n	<dd>Спектакъл \"Дивият изток\" с участието на Иван Кулеков и Татяна Лолова</dd>\r\n	<dd>Филм на БНТ за група \"Носталгия\" в предаването \"Минаха години\"</dd>\r\n	<dd>Първо издание на Празниците на старата градска песен – Монтана</dd>\r\n\r\n	<dt>1998 г</dt>\r\n	<dd>Тържество по повод 100 години от рождението на Дико Илиев</dd>\r\n	<dd>Изяви по повод 115 години от създаването на читалище \"Разум\"</dd>\r\n	<dd>Първа награда от фестивала \"Златен кестен\" за група \"Носталгия\"</dd>\r\n	<dd>Празници на старата градска песен – гостува Маргрет Николова</dd>\r\n\r\n	<dt>1999 г</dt>\r\n	<dd>Тържество по повод 70 години от създаването на Духовия оркестър</dd>\r\n\r\n	<dt>2001 г</dt>\r\n	<dd>Създава се мъжка вокална група с ръководител Снежина Георгиева</dd>\r\n	<dd>Спечелен проект \"Изкуство срещу дрогата\"- донор Световна банка</dd>\r\n	<dd>Проект \"Фолклорна палитра\" – донор Национален фонд \"Култура\"</dd>\r\n	<dd>Участие на мъжка вокална група в Международен песенен фестивал в Банска Бистрица – Словакия – награда на журито</dd>\r\n	<dd>В читалището идва ТА \"Пъстрина\" с ръководител Първан Първанов</dd>\r\n\r\n	<dt>2002 г</dt>\r\n	<dd>Участие на Духовия оркестър в Международен фестивал в град Шиклош –Унгария</dd>\r\n	<dd>Концертно турне на ТА \"Пъстрина \" в Италия, Франция и Германия</dd>\r\n\r\n	<dt>2003 г</dt>\r\n	<dd>Тържества по повод 120 години от създаването на читалище \"Разум\"</dd>\r\n	<dd>Участие на мъжки хор в Международен песенен фестивал \"Бохуслав Мартину\" – гр Пардубице, Чехия</dd>\r\n	<dd>Участие на ТА „Пъстрина\" в 3 Международни фолклорни фестивали – Италия</dd>\r\n	<dd>Спечелен проект \"Център за превенция на наркомании\" – донори ПРООН, Холандското правителство, Американска агенция за развитие, Министерство на културата</dd>\r\n\r\n	<dt>2004 г</dt>\r\n	<dd>Тържество по повод 75 години от създаването на Духов оркестър</dd>\r\n	<dd>Участие на ТА\"Пъстрина\" в МФФ – гр Тревизо Италия</dd>\r\n\r\n	<dt>2005 г</dt>\r\n	<dd>Спечелен проект \"Ден на Европа\" – донор Фондация за развитие „Читалища\"</dd>\r\n	<dd>Създадено Литературно студио за млади поети</dd>\r\n\r\n	<dt>2006 г</dt>\r\n	<dd>Тържество по повод 70 годишнина на Тереза Лилова – дългогодишна самодейка</dd>\r\n	<dd>Среща – разговор с Надежда Захариева – зам министър на културата</dd>\r\n	<dd>Концертно турне на ТА \"Пъстрина\" във Франция и Португалия</dd>\r\n	<dd>Участие на мъжки хор в тържества по повод 800 години от създаването на гр Лайпциг- Германия</dd>\r\n	<dd>Създаване на фото клуб към читалище \"Разум\"</dd>\r\n	<dd>Концерт по повод 50 години от създаването на народния хор</dd>\r\n\r\n	<dt>2008 г</dt>\r\n	<dd>125 години от създаването на читалище \"Разум\"</dd>\r\n\r\n	<dt>2009 г</dt>\r\n	<dd>Тържество по повод 30 г от създаването на ОШИ „Д. Христов\"</dd>\r\n	<dd>Участие на мъжки хор в Международен фестивал в Словакия – І награда</dd>\r\n	<dd>Честване 80 г на Духов оркестър „Дико Илиев\"</dd>\r\n\r\n	<dt>2010 г</dt>\r\n	<dd>Концерти на ТА „Пъстрина\" в гр Крайова и гр Каракал - Румъния</dd>\r\n	<dd>Участие на мъжки хор в Международен фестивал – гр Превеза, Гърция – І награда</dd>\r\n\r\n	<dt>2011 г</dt>\r\n	<dd>Честване 30 г от създаването на ТА „Пъстрина\"</dd>\r\n	<dd>Концерт на мъжки хор по повод 100г от създаването на СНЧ –НДК София</dd>\r\n	<dd>Участие на ТА „Пъстрина\" в Междунаподен фестивал – гр Ниш, Сърбия</dd>\r\n	<dd>Концерт „Кръщавка\" по повод създаването на ФФ „Огоста\"</dd>\r\n\r\n	<dt>2012 г</dt>\r\n	<dd>Участие на мъжки хор в Международен фестивал – гр Краков, Полша – ІІ награда</dd>\r\n	<dd>Участие на народен хор към ФФ „Огоста\" в Международен фолклорен фестивал – гр Кюстендил – І награда</dd>\r\n	<dd>Участие на ТА\"Пъстрина\" в Международен фестивал – Италия</dd>\r\n\r\n	<dt>2013 г</dt>\r\n	<dd><b>130 г от основаването на НЧ „Разум 1883\"</b></dd>\r\n\r\n</dl>\r\n',NULL,NULL,NULL),(3,'bg','2013-03-04 14:15:43','2013-03-04 14:15:43',1,1,7,NULL,NULL,'Дейности',NULL,'<p>Читалище \"Разум\" винаги се е ползвало с голям социален престиж, историческо доверие и потенциал да откликва на новостите  и предизвикателствата на времето.Днес има нелеката задача да се превърне в модерен общностен център, с възможност да решава културни , информационни, социални и граждански функции. Продължава активно да участва в културния живот на нашия град, а художествените колективи с чест защитават достиженията на българския фолклор по европейските сцени. Над 260 самодейци, изпълнители и възпитаници са включени в съставите на Духов оркестър \"Дико Илиев\" , Танцов Ансамбъл \"Пъстрина\", Група за стари песни и шлагери \"Носталгия\", Мъжки хор, Фолклорна формация \"Огоста\", народен хор, фото клуб, литературно студио и Школа по изкуствата.</p>\r\n<p>Читалището е място на знанието, то развива талантите и дава шанс на всеки, който иска да изрази себе си с езика на творчеството и който носи таланта и куража да сподели мислите си с другите. Школата по изкуствата е първото стъпало към приобщаване на децата и младежите в света на музиката, танца и изобразителното изкуство.</p>',NULL,NULL,NULL),(4,'bg','2013-03-04 14:16:06','2013-03-04 14:16:06',1,1,7,NULL,NULL,'Проекти','спечелени от читалище \"РАЗУМ 1883\" - гр Монтана','<h5><u>1. 2001 г – ПРОЕКТ \"ИЗКУСТВО СРЕЩУ ДРОГАТА\"</u> – финансиран от Световна банка</h5>\r\n<p><b>Стойност - 7518, 03 лв / 3 300 долара /</b></p>\r\n<p><b>Същност на проекта:</b></p>\r\n<p>\r\n    Проект \"Изкуство срещу дрогата\" е шестмесечна информационна кампания, насочена към младежите от 14 до 18 г възраст. Целта на проекта е широкото им\r\n    информиране, обучение и запознаване с рисковете, симптомите и различните ситуации, които отключват и поощряват злоупотребата с дрога. Осъществяване на\r\n    обществен диалог и създаване на информирана среда от младежи, родители, учители, психолози, лекари и др, които да изострят вниманието към такъв обществено\r\n    опасен проблем, какъвто са наркотиците.\r\n</p>\r\n<p>\r\n    Включване на младежите в организация и съавторство в кампании против дрогата чрез конкурси, концерти и хепънинги и др, създаването на непримиримост у тях\r\n    към наркоманията, поощряване активността им да дискутират открито проблема и търсят сами начини за превенция чрез клубове, неформални младежки организации\r\n    и др.\r\n</p>\r\n<p>\r\n    Чрез средствата на изкуството осъществяване на пряк емоционален контакт с младежи в рискова възраст, даване на възможност всеки да изрази своята позиция.\r\n</p>\r\n<p><b>Дейности:</b></p>\r\n<ul>\r\n    <li>Дискусионен семинар\r\n    <li>Музикално-танцов спектакъл \"Любов за живот\"\r\n    <li>Авторска изложба Т. Деков на тема \"Спин, наркомания и екология\"<br/>\r\n    / 14 изложби по всички училища в Монтана /\r\n    <li>Отпечатване на плакати, брошури 200 - бр / за деца и родители /\r\n    <li>Младежки конкурси – за есе и рисунка\r\n    <li>Рок концерт на група ПИФ\r\n</ul>\r\n<p><b>Целеви групи:</b> над 10000 младежи от всички училища на града.</p>\r\n\r\n\r\n\r\n\r\n\r\n<h5><u>2. 2001 г - ПРОЕКТ \"ФОЛКЛОРНА ПАЛИТРА\" – финансиран от НФ \"КУЛТУРА\"</u></h5>\r\n<p><b>Стойност - 2250 лв </b></p>\r\n<p>\r\n    <b><u>\"Фолклорна палитра</u></b>\r\n    <b>\" – 4.VІ.2001 г – Концерт на празника на града Свети дух </b>\r\n</p>\r\n<p><b>Същност и цели на проекта:</b></p>\r\n<p>Целта на проекта е да поощри дейността на читалищата в издирването, съхранението и разпространението на фолклорното богатство на Северозападна България:</p>\r\n<ul>\r\n    <li>да приобщи различните общности и създаде възможност за интеграция и взаимно опознаване фолклорните традиции на различните етноси\r\n    <li>да осигури приемственост между поколенията и даде възможност на младежите и децата да се запознаят с традиционни за миналото на региона занаяти и автентични обряди и практики\r\n</ul>\r\n<p><b>Дейност:</b></p>\r\n<ul>\r\n    <li>кръгла маса по въпроси на фолклора с професор Холевич\r\n    <li>концерт на 10 читалища от Община Монтана\r\n    <li>алея на занаятите в градския парк – пресъздаване на обичаи и практики\r\n    <li>конкурс за детска рисунка на народна носия\r\n    <li>конкурс за най-красив костюм – \"Баба и внуче\"\r\n    <li>надиграване с ръченица\r\n</ul>\r\n<p><b><u>Фолклорен концерт \"Надпели се, надиграли се\"</u> - 23.10.2001 г </b></p>\r\n<ul>\r\n    <li>фолклорен концерт - ч-ще \"Разум\" и още 5 селски читалища\r\n</ul>\r\n\r\n\r\n\r\n\r\n\r\n<h5><u>3. 2003 – 2004 г - ПРОЕКТ \"ЦЕНТЪР ЗА ПРЕВЕНЦИЯ\"</u> - ФИНАНСИРАН ОТ Министерството на културата, Програма МАТРА, UNDP</h5>\r\n<p><b>Стойност - 13 000 лв</b></p>\r\n<ul>\r\n    <li>12 обучителни семинара за превенция и дискусии с ученици и родители\r\n    <li>постоянна радио рубрика по радио Канал М <b></b>\r\n    <li>конкурс за рисунка и есе на тема \"Дрога или живот\"<b></b>\r\n    <li>изложба на рисунки на тема – \"Дрога или живот\"<b></b>\r\n    <li>издаване на пътеводител за превенция – четиво за всички<b></b>\r\n</ul>\r\n\r\n\r\n\r\n\r\n\r\n<h5><u>4. 2003 г - ПРОЕКТ \"НЕОТЛОЖНИ СТРОИТЕЛНИ НУЖДИ\"</u> - ремонт на санитарен възел , финансиран от Проект \"Читалища\"</h5>\r\n<p><b>Стойност – 2600 лв</b></p>\r\n\r\n\r\n\r\n\r\n\r\n<h5><u>5. 2004 г - НЕОТЛОЖНИ СТРОИТЕЛНИ НУЖДИ - \" РЕМОНТ НА ДУХОВА ЗАЛА\"</u> –  финансирано от МК</h5>\r\n<p><b>Стойност - 5000 лв</b></p>\r\n\r\n\r\n\r\n\r\n\r\n<h5><u>6. 2007 г - ПРОЕКТ \"ДЕН НА ЕВРОПА\"</u> – финансиран от Фондация за развитие \"Читалища\"</h5>\r\n<p><b>На стойност - 3 500 лв</b></p>\r\n<p><b>Цел на проекта:</b></p>\r\n<p>\r\n    Проектът за отбелязване деня на Европа в рамките на Информационната и комуникационна програма на Делегацията на\r\n</p>\r\n<p>\r\n    Да повишат осведомеността на обществото за процеса на присъединяване на България към Европейския съюз и ползите от членството си в него.\r\n</p>\r\n<p>\r\n    Да се запознаят с историята на ЕС, неговите институции\r\n</p>\r\n<p><b>Дейност:</b></p>\r\n<ul>\r\n    <li>Дискусионен форум \" Европа за нас и ние за Европа\"\r\n    <li>Радио рубрика\r\n    <li>Конкурс за рисунка \"Европа – позната и различна\"\r\n    <li>Концерт \"Ние сме в Европа\"\r\n</ul>\r\n\r\n\r\n\r\n\r\n\r\n<h5><u>7. 2006 г - НЕОТЛОЖНИ СТРОИТЕЛНИ НУЖДИ</u> – ремонт на входно фоайе, финансиран от МК</h5>\r\n<p><b>На стойност– 5000 лв</b></p>\r\n\r\n\r\n\r\n\r\n\r\n<h5><u>8. 2007 г - Допълваща субсидия – МК - смяна на дограма на административни помещения и закупуване на компютърна техника</u></h5>\r\n<p><b>На стойност – 4000 лв</b></p>\r\n\r\n\r\n\r\n\r\n\r\n<h5><u>9. 2008 г - Допълваща субсидия – МК – ремонт на танцова зала и закупуване на музикални инструменти</u></h5>\r\n<p><b>На стойност – 7000 лв</b></p>\r\n\r\n\r\n\r\n\r\n\r\n<h5><u>10. Координиране на обучителни компютърни семинари за селски читалища по Проект \"Селските читалища – центрове за информационни услуги\" на сдружение АГОРА</u></h5>\r\n\r\n\r\n\r\n\r\n\r\n<h5><u>11. 2009 г – Допълваща субсидия МК – за закупуване на детски национални костюми</u></h5>\r\n<p><b>На стойност – 5000 лв</b></p>\r\n\r\n\r\n\r\n\r\n\r\n<h5><u>12. 2010 г - допълваща субсидия - 5 000лв</u></h5>\r\n<h5><u>13. 2011 г - Допълваща субсидия </u></h5>\r\n<p><b>На стойност - 2000 лв</b></p>\r\n\r\n\r\n\r\n\r\n\r\n<h5><u>РЕКАПИТУЛАЦИЯ:</u></h5>\r\n<p>\r\n    <b>1. Проект \"Изкуство срещу дрогата\" - 7 518, 03 лв</b>\r\n</p>\r\n<p>\r\n    <b>2. Проект \"Фолклорна палитра\" - 2 250, 00 лв</b>\r\n</p>\r\n<p>\r\n    <b>3. Проект \"Център за превенция\" - 13 003, 59 лв</b>\r\n</p>\r\n<p>\r\n    <b>4. Проект \"Неотложни строителни нужди\" - 2 600, 00 лв</b>\r\n</p>\r\n<p>\r\n    <b>5. Неотложни строителни нужди - 5 000, 00 лв</b>\r\n</p>\r\n<p>\r\n    <b>6. Проект \"Ден на Европа\" - 2 700, 00 лв</b>\r\n</p>\r\n<p>\r\n    <b>7. Неотложни строителни нужди - 5 000, 00 лв</b>\r\n</p>\r\n<p>\r\n    <b>8. Допълваща субсидия - 4 000, 00 лв</b>\r\n</p>\r\n<p>\r\n    <b>9. Допълваща субсидия - 7 000, 00 лв</b>\r\n</p>\r\n<p>\r\n    <b>10.Координиране по проект на АГОРА - 1 000, 00 лв</b>\r\n</p>\r\n<p>\r\n    <b>11.Допълваща субсидия - 5 000, 00 лв </b>\r\n</p>\r\n<p>\r\n    <b>12. Допълваща субсидия - - 5 000, 00 лв</b>\r\n</p>\r\n<p>\r\n    <b>13. Допълваща субсидия - - 2 000 ,00 лв</b>\r\n</p>\r\n<p>\r\n    <b> ОБЩО: 62 071, 62 лв </b>\r\n</p>\r\n<p>\r\n    <b> / шестдесет и две хиляди, 71 лв и 62 ст / </b>\r\n</p>\r\n',NULL,NULL,NULL),(7,'bg','2013-03-04 14:20:57','2013-05-14 12:07:16',1,1,7,NULL,NULL,'Представяме Ви','','<p>Снимка и име на читалищен деятел,\r\nк-в или човек, спечелил награда,\r\nпо повод годишнина,\r\nдългогодишен самодеец и др</p>\r\n','','',''),(9,'bg','2013-03-04 14:22:10','2013-03-04 14:22:10',1,1,7,NULL,NULL,'Контакти',NULL,'<p>Народно читалище \"Разум 1883\" – гр МОНТАНА</p>\r\n<p>Бул. \"ТРЕТИ МАРТ\" 88</p>\r\n<p>Гр. МОНТАНА</p>\r\n<pre>Тел. 096 / 305 671\r\n     096 / 306 393</pre>\r\n<iframe width=\"425\" height=\"350\" frameborder=\"0\" scrolling=\"no\" marginheight=\"0\" marginwidth=\"0\" src=\"https://maps.google.com/maps/ms?msa=0&amp;msid=209559185273103924720.0004d970968ad6ffb3178&amp;hl=bg&amp;ie=UTF8&amp;t=m&amp;source=embed&amp;ll=43.406684,23.225634&amp;spn=0.002728,0.00456&amp;z=17&amp;output=embed\"></iframe><br /><small>Вижте <a href=\"https://maps.google.com/maps/ms?msa=0&amp;msid=209559185273103924720.0004d970968ad6ffb3178&amp;hl=bg&amp;ie=UTF8&amp;t=m&amp;source=embed&amp;ll=43.406684,23.225634&amp;spn=0.002728,0.00456&amp;z=17\" style=\"color:#0000FF;text-align:left\" target=\"_blank\">НЧ \"Разум 1883\" гр. Монтана</a> на по-голяма карта</small>\r\n',NULL,NULL,NULL),(11,'bg','2013-03-06 16:15:38','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Светослав Стефанов Боцев','председател на НЧ \"Разум 1883\" – гр Монтана','<img alt=\"\" style=\"float: right; margin: 0px 0px 10px 10px;\" src=\"/images/sv.bocev.png\" title=\"Светослав  Боцев – изпълнителят в дясно\">\r\n<p>Светослав Боцев е потомствен самодеец в читалището. Вероятно, любовта към сцената и пеенето се предава генетично, защото едни от най-активните самодейни изпълнители на народни песни и танци в Белоградчик са неговите баба и дядо, а родителите му активни участници в т.н. Ансамбъл през 50-те години на миналия век - граждански хор и симфоничен оркестър в читалище \"Разум\" Монтана. По-късно майка му - Вера Боцева, ушива най-красивите тоалети на солистите от самодейната оперета при читалището, които се пазят и до днес.</p>\r\n<p>Светослав Боцев пее в ученическия хор на Гимназията, а от 1980г. става председател на смесения хор в читалището под диригенството на Чавдар Маждраков.</p>\r\n<p>Отначало на шега, но в последствие се оказа много успешно, създаването на мъжки хор при читалището през 2000г., душата и организатора на който е г-н Боцев. Съставът има много почитатели, всяка година участва в престижни Международни хорови фестивали и всеки път завоюва една от първите награди, създава традиции и значими концертни изяви.</p>\r\n<p>Няколко мандата е бил член на Настоятелството на читалището  и зам председател, а от 2010г. – председател.</p>\r\n','','Светослав Боцев',''),(12,'bg','2013-03-07 15:33:18','2013-05-14 14:45:28',1,1,7,NULL,NULL,'Павлина Папуркова','','<p><img style=\"float: right; margin: 0px 0px 10px 10px;\" src=\"/images/PavlinaPapurkova1.png\" alt=\"\">Павлина Папуркова  е от поколението читалищни дейци, оставили трайна диря в културния живот на читалището и нашия град.</p>\r\n<p>Започва да пее през 1958 в смесения хор на читалището под диригенството на Пънто Бочев, а впоследствие  в опертния  колектив. Изявява  се като солистка и години наред е примадона на оперетата. В репертоара й са включени най-интересните  заглавия от жанра – оперетите \"Българи от старо време\", \"Целувката на Чанита\", \"Прилепът\", \"Бедният студент\", \"Волният вятър\", \"Хубавата Елена\", \"Службогонци\", \"Моята прекрасна лейди\", \"Графиня Марица\" и др.</p>\r\n<p>Носител е  на много  престижни награди – златни и сребърни медали  от  републикански фестивали  на художествената самодейност и орден \"Кирил и Методий\" – І и ІІ степен.</p>\r\n<p>През 1998 година, заедно с други самодейци, става основател на Групата за стари песни и шлагери \"Носталгия\", с която участна в   множество концерти и печели награди от Национални фестивали на старата градска песен - \"Златен кестен\" – Петрич, \"Дионисополис\" – Балчик, \"Море и спомени\" – Варна и др.</p>\r\n<img alt=\"\" style=\"float: left; margin: 0px 10px 10px 0px;\" src=\"/images/PavlinaPapurkova2.png\" title=\"Павлина Папуркова\">\r\n<p>Павлина Папуркова беше активен радетел за развитието и популяризирането на читалищното дело. Винаги се е ползвала с  голям авторитет сред самодейците и обществеността на град Монтана.</p>\r\n<p>Читалището се сбогува с нея на 1 март 2013 г  – Деня ня самодееца.</p>\r\n','','',''),(13,'bg','2013-03-26 17:11:46','0000-00-00 00:00:00',1,1,2,NULL,NULL,'Възпитаници на школата по изкуствата – с нови награди от фестивал','','<p>На 23 и 24 март в гр София се проведе ІХ Национален детско-юношески конкурс с международно участие  за музикално и танцово изкуство \"Орфеева дарба\".\r\nУчениците от класа на Мариана Цолова участваха в 2 раздела – пиано и забавна и поп музика. В раздел \"Забавна и поп музика\" взеха участие 4-ма ученика. Еделина Николаева спечели сребърен медал, покана от Арт център \"Карнолски\" за участие във фестивал в Слънчев бряг и покана за запис в радиото, а Мила Емилова – поощрителна награда.</p>\r\n<p>С две награди се поздравиха и участниците в раздел \"пиано\" – Златен медал за Анна-Мария Асенова и поощрителна награда за Деница Паменова.\r\nПреподавателката Мариана Цолова получи 2 дипломи за високи творчески постижения с участници – лауреати.</p>\r\n<p>В същия конкурс взе участие и ученичката от класа на Цветана Илиева – Ива Емилова\r\nкоято също се завърна с две награди – сребърен медал за изпълнение на народна песен и поощрителна награда в раздела за забавна и поп музика.</p>','',NULL,NULL),(14,'bg','2013-03-28 15:43:20','2013-05-08 16:36:06',1,1,2,NULL,NULL,'Школата по изкуствата представи своя традиционен пролетен концерт','','<p>На 21.03.2013г в голямата зала на Младежкия дом, възпитаниците на ОШИ \"Добри Христов\", представиха своя традиционен концерт \"Пролетно равноденствие\". \r\nВ концертът взеха участие 46 деца на възраст от 4 до 16 години. Участваха деца от музикалния, танцовия и кукления отдел на школата. Учениците изпълниха пиеси от предкласика до съвременна музика. Направиха впечатление изпълненията на Емил Ивайлов – ксилофон и  Ива Емилова – народно пеене, носител на много награди от Национални конкурси и надпявания, както  и учениците по китара.</p>\r\n<p>Сред изпълненията на пиано се откроиха Ива Пламенова и Елица Искренова, малките певци Денислав и Никол, а децата от танцовата школа танцуваха  латино танци. Концертът завърши с изпълнението на кукления театър \"Танц на пингвините\", в който на финала се вкючиха всички участници.</p>','','',''),(15,'bg','2013-03-28 15:46:38','0000-00-00 00:00:00',1,1,2,NULL,NULL,'Школата по изобразително изкуство с нова изложба',NULL,'<p>На 18.02.2013г в зала \"Кутловица\" – Монтана, учениците от класа на Петър Григоров, преподавател по рисуване в Обединената школа по изкуствата, подредиха своята 5 поредна изложба. Изложени бяха 120 творби,  предимно графика и живопис, на деца от 5 до 18 години.</p>\r\n<p>Впечатление правят етюдите и портретите на 18-годишния Вениамин  Василев.</p>\r\n<p>Освен много добри пейзажи и портрети, учениците представят добри шаржове и политически карикатури. Една от тях препраща към кмета на Монтана Златко Живков, изобразен в костюм на Супермен и поставен върху постамент. Групата на художниците в средната възрастова група пък се представя  с интересни колажи.</p>\r\n<p>Голяма част от децата имат много сериозни живописни постижения.</p>',NULL,NULL,NULL),(16,'bg','2013-03-29 11:07:53','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Настоятелство','Председатели и секретари на Читалище \"Разум 1883\"','<img alt=\"\" style=\"float: right; margin: 0px 0px 10px 10px;\" src=\"/images/GavrilDushkov.png\" title=\"Гаврил Душков\">\r\n<p><b>Гаврил Душков – Първият известен председател на читалището - 1984 до 1922 г</b></p>\r\n<p><b>Настоятелство /основатели/ - Кирил Греков, Тодор Денчев, Спас Иванов</b></p>\r\n<p>1927 г. - Владимир Симеонов – председател</p>\r\n<p>1928 г - Никола Сапунджиев – председател</p>\r\n<p>Настоятелство - Владимир Симеонов, Любомир Коларов, Михаил Наумов, Ст Тодоров</p>\r\n<p>1930 г. - Никола Сапунджиев – председател</p>\r\n<p>Славко Стефанов - подпредседател</p>\r\n<p>Гаврил Ленков – секретар</p>\r\n<p>1932 г. - Васил Топчийски – председател</p>\r\n<p>1937 г. - Васил Топчийски – председател</p>\r\n<p>Езекия Грозданов - подпредседател</p>\r\n<p>Асен Геров – секретар</p>\r\n<p>1938 г. - Георги Попов – председател</p>\r\n<p>Настоятелство - Димитър Греков, Янаки Панов, Кирил Пенков,Цено Беляшки</p>\r\n<p>1944 г. - Никола Алексиев – председател</p>\r\n<p>1946 г. - Емануил Попов- председател</p>\r\n<p>Стефан Станев – заместник председател</p>\r\n<p>Цено Беляшки – секретар</p>\r\n<p>1947 г. - Спас Русинов – председател</p>\r\n<p>Илия Савов – секретар</p>\r\n<p>1948 г - Спас Русинов - председател</p>\r\n<p>Емануил Попов – заместник председател</p>\r\n<p>Илия Савов – секретар / щатен /</p>\r\n<p>1948 г. - Жечо Боздуганов – председател</p>\r\n<p>Илия Савов – секретар</p>\r\n<p>1952 г - Жечо Боздуганов – председател</p>\r\n<p>Райна Данаилова – секретар</p>\r\n<p>1954 г. - Борис Грънчаров – председател</p>\r\n<p>Георги Александров – зам председател</p>\r\n<p>Борис Дренчев - секретар</p>\r\n<p>1955 г - Янаки Панов – председател</p>\r\n<p>Георги П. Стоянов – секретар</p>\r\n<p>1956 г. - Георги Тодоров – председател</p>\r\n<p>Апостол Кръстев – секретар и директор на Детска музикална школа</p>\r\n<p>1957 г. - Мато Матов – предеседател</p>\r\n<p>1958 г. - Симеон Момчилов – председател</p>\r\n<p>Ангел Еленков - секретар</p>\r\n<p>1960 г. - Илия Костов – председател</p>\r\n<p>Ахмед Бабишев - секретар</p>\r\n<p>1961 г. - Петър Ангелов – щатен председател</p>\r\n<p>Ахмед Бабишев - секретар</p>\r\n<p>Павел Илиев</p>\r\n<p>1963 г. - Симеон Момчилов – председател</p>\r\n<p>Ангел Еленков – секретар / щатен /</p>\r\n<p>1964 г. - Симеон Момчилов – председател на обществени начала</p>\r\n<p>Илия Костов – секретар /щатен/</p>\r\n<p>1965 г. - Петър Ангелов - председател / щатен /</p>\r\n<p>Цеко Дичев - секретар / щатен /</p>\r\n<p>1967 г. - Стоян Папурков – председател</p>\r\n<p>Ангел Еленков – секретар</p>\r\n<p>1968 г. - Симеон Момчилов – председател</p>\r\n<p>Ангел Еленков - секретар</p>\r\n<p>1973 г. - Симеон Момчилов – председател</p>\r\n<p>Вера Маринова – зам председател</p>\r\n<p>Петър Георгиев - секретар</p>\r\n<p>1977 г - Александър Василев секретар</p>\r\n<p>1977 г - Иван Коларов - секретар</p>\r\n<p>1979 г - Илия Младенов – председател / щатен /</p>\r\n<p>Иван Стефанов – секретар</p>\r\n<p>1981 г - Рангел Спасов - председател</p>\r\n<p>Асен Иванов - секретар</p>\r\n<p>1982 г - Рашко Григоров – председател</p>\r\n<p>Петрана Петрова – секретар</p>\r\n<p>1984 г Иван Нисторов - председател</p>\r\n<p>Емил Ананиев – секретар</p>\r\n<p>1985 г - Никола Кръстев – председател</p>\r\n<p>Емил Ананиев – секретар</p>\r\n<p>1989 г - Лазар Савов - секретар</p>\r\n<p>1992 г - Петко Котларски - председател</p>\r\n<p>1993 г Пенка Вацкова - председател</p>\r\n<p>1997 г. - Пенка Вацкова – председател</p>\r\n<p>Серафим Калоянов – секретар</p>\r\n<p>2000 г. - Весела Коларска – председател</p>\r\n<p>Серафим Калоянов – секретар</p>\r\n<p>2001 г. - Весела Коларска – председател</p>\r\n<p>Светослав Боцев – зам председател</p>\r\n<p>Бойка Серафимова – секретар</p>\r\n<p>2010 г - Илия Замфиов Илиев – председател</p>\r\n<p>Светослав Боцев – зам председател</p>\r\n<p>Бойка Серафимова – секретар</p>\r\n<p>Настоятелство – Л. Лилов,П. Първанов,Нина Петрова, Драгомир Григоров</p>\r\n<p>2012 г - Светослав Боцев – председател</p>\r\n<p>Даниела Данаилова - зам председател</p>\r\n<p>Бойка Серафимова – секретар</p>\r\n<p>Настоятелство – Рашко Григоров, Нина Петрова, Деян Димитров,Станчо Манчевски</p>\r\n','','',''),(18,'bg','2013-03-29 11:24:50','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Дико Илиев Диков','автобиография','<img alt=\"\" style=\"float: right; margin: 0px 0px 10px 10px;\" src=\"/images/DikoIliev.png\" title=\"Последният концерт, дирижиран от Дико Илиев\">\r\n<p>Роден съм на 15.02.1898г. в с.Карлуково, Ловешки окръг в бедно селско семейство.</p>\r\n<p>Учил съм в родното си село до 4-то отделение, но поради липса на средства да ме изпратят в Луковит, не съм продължил образованието си. Налагало се е да помагам вкъщи на баща си като паса овцете – наши и чужди.</p>\r\n<p>През 1911г. по мое желание, ме изпращат в Орхание /Ботевград/ като музикантски ученик. Започнах да се уча да свиря на тромба. Мои диригенти са били Димитър Хараламбиев, Йосиф Кубичек. При 11 месечна военна служба ме направиха ефрейтор. През 1912г. участвах в Балканската война с 16-ти пехотен Ловчански полк по направление на Люлебургас, Чаталжа с духовия оркестър. След войната отново се връщам като сигналист в Ботевград. През 1915г. участвам в Първата световна война като с духовия оркестър на 16-ти Ловчански полк в състава на Първа българска армия потегля през Своге и Годеч към Сърбия – установява се в гр.Кретово.</p>\r\n<p>През 1916г. Щестнадесети Ловчански полк заминава на помощ на Трета българска армия в Добруджа – в с.Максинени. /Фронтът се установява при устието на р.Дунав и р.Серет/. Тук в 1917г. на Серетския фронт написах първото си хоро \"Искърско хоро\"</p>\r\n<p>/в с.Бозац/. То ми напомняше за родния край и трябваше да весели войниците в тежките дни на войната. През 1918г. шестнадесети Ловчански пехотен полк е натоварен отново на влака – връща се в Македония – минаваме през Браила, Плоещ, Букурещ, Смедарово и в Македония полкът заема позиция на кота 1050.</p>\r\n<p>Музикантски ученик съм от 11 май 1911г. до 1 април 1918, след това съм назначен на подофицерска музикантска длъжност до 9.10.1918г. – тогава е потушено войнишкото въстание и макар и трудно се връщам в с.Карлуково. Тук се занимавам със земеделие и свиря по сватби и кръщенета.</p>\r\n<p>На 11 май 1919г. след изпит постъпвам като оркестрант във военното училище – София. Свиря на баритон в духовия оркестър, а в салонния оркестър – I ви тромбон. Диригент е Тодор Хаджиев.</p>\r\n<p>От музикантите на гвардейския оркестър и на Военното училище</p>\r\n<p>е съставен сборен симфоничен оркестър, който трябва да обслужва \"Оперната дружба\", която 3 години по-късно става Държавна опера. Тук съм свирил около 9 месеца – на I- ви тромбон.</p>\r\n<p>През 1919г. се оженвам и се връщам в родното си село през 1920г.</p>\r\n<p>В продължение на 11 години съм свирил от едно в друго село, давали са ми земя за временно ползуване. Изселвам се от Карлуково и се заселваме в с.Войводово, Врачанско – с хубави и плодородни ниви. Там остават родителите ми и брат ми, а аз отивам в с.Букьовци /сега Мизия/ през 1922г.</p>\r\n<p>Там ми се дават 25 дк. земя от общинската. Тук свиря и композирам различни хора, организирам оркестър, който през 1928г. е особено добър.</p>\r\n<p>Хората, които съм написал през този период са: \"Букьовско\", \"Грънчарско\", \"Самоковско\". През 1930г. съставям духов оркестър в с.Комарево, Врачанско. Тук написвам хорото \"Веселият Иванчо\".</p>\r\n<p>През 1931г. постъпвам в Оряхово в музикантския взвод. През 1932г. диригент на военния оркестър е Александър Вейнар. Той е мой основен учител по хармония. През 1935г. моята 14-годишна дъщеря Александрия умира – на нея посветих хорото си \"Александрийка\".</p>\r\n<p>След това заминавам за София при капелмайстор Касабов – като баритонист и старши на оркестъра. През 1938г. сме изнасяли концерти по радио София, а самостоятелно съм свирил на акордеон-хармоника народни песни и хора по радио София.</p>\r\n<p>През август 1938г. се връщам отново в Оряхово в 36-ти пехотен Козлодуйски полк. Тук ми се ражда дъщеря, която нарекох Александрия. Сега живея при нея и семейството й в Михайловград, тя е инженер-геолог и е моята радост и утеха в старините ми.</p>\r\n<p>През 1948г. в нейна чест написах хорото \"Веселото Санче\".</p>\r\n<p>През тези години написах хората: \"Дунавско\", \"Майски цветя\", \"Пробуждане на пролетта\", \"Майско утро\", \"Игривата циганка\", \"Скок\", \"Дайчово хоро\", \"Билка\", \"Лудо-младо\", \"Народна душа\", \"Тодорка\", \"Нашето Ленче\", \"Селски празник\", \"Буйна младост\", \"Край чешмата\", \"Зиг-заг\", \"Оряховски юнак\", \"Добруджанско хоро\", \"Наша гордост\" и \"Жетварски песни\".</p>\r\n<p>От 1939 до 1944г. написвам \"Веселите близнаци\" и \"Олеле, бате Иване\" – хора. През 1944г. 36-ти Козлодуйски полк е в Босилеград до Скопие. След I-та фаза на войната полка се завръща в Оряхово. Тук написвам китката \"Народно ехо\". До 1946г. съм основател на профдружеството на музикантите. До 1947г. съм организирал 19 проф-</p>\r\n<p>музикантски групи в околията, изнасяхме концерти в градската градина, разпространявах нотен материал сред музикантите. През 1947г. полка се разформирова и аз отидох в Сливен, където в 11-ти пехотен полк служих само 7 месеца. Тук написах марша \"Сливенци при Драва\", вдъхновен от проявения героизъм и славни победи на Сливенския полк през Отечествената война. С този марш дълги години радио София откриваше сутрин своята програма.</p>\r\n<p>Полкът в Оряхово се възстановява и отново се връщам там. На 50-годишна възраст, през 1948г. се явих на изпит в София – практически по дирижиране, изпит по хармония и оркестрация, по политическа подготовка, устави и доклад върху характерните особености на българската народна музика. Издържах изпита и след 3-месечен стаж в оркестъра на авиацията при диригента Байдунов, бях удостоен с офицерско звание – лейтенант и станах диригент на Оряховския духов военен оркестър. Освен военната служба се редяха концертни програми на оркестъра пред войниците, в читалищния салон, в градската градина, в селата на Оряховска околия. Участвам активно и във войнишката художествена самодейност, в градската читалищна самодейност и др.</p>\r\n<p>Основател съм на детската музикална школа в Оряхово.</p>\r\n<p>През 1955г. съм повишен в чин капитан и длъжност командир на батальон.</p>\r\n<p>Три години преди пенсионирането си бях в Белоградчишкия полк.</p>\r\n<p>През декември 1957г. се пенсионирах.</p>\r\n<p>За този период написах хората: \"Букет от песни\", \"Ганкино\", \"Кръшен венец\", маршовете \"Напред другари\", \"Катюша\", \"Осма Ботевградска дивизия\", \"Октомври-марш\", \"20 години народна власт\", \"Козлодуйци през 1 май\", \"Младежка клетва\", \"Изборна победа\", \"Лехчевски бригадир\", \"Механизатор\" и др, рапсодиите \"Танц на жетварките\" – написана през 1950г. и китката \"Ехо от Съветския съюз\".</p>\r\n<p>За рапсодията \"Танц на жетварките\" съм оркестрирал сам за симфоничен оркестър. Партитурата ми се намира в Клуба на културните дейци във Враца – подарих я през 1973г. за симфоничния оркестър Враца при честване на 75-годишния ми юбилей.</p>\r\n<p>Освен дирижиране и композиране, съм съставил духови оркестри във Врачански и Михайловградски окръзи – в селата Хайредин, Септемврийци, Бърдарски Геран, Мадан, Лехчево, Бързине, Рогозен, Ботево, Мизия, Гложене.</p>\r\n<p>Моето творчество е представено от: 42 народни хора, 16 марша, 7 китки, 1 рапсодия. По-голяма част от тях се изпълняват по радио София и някои са издадени на грамофонни плочи.</p>\r\n<p>През 1973г. се чества моята 80-годишнина в Михайловград в Драматичния театър и във Враца, където бе изнесен концерт от гвардейския оркестър от София. На 24 май 1979г. ме удостоиха със звание \"Заслужил деятел на културата\".</p>\r\n<p>През лятото на 1978г. другаря Тодор Живков при посещението си в Михайловград се среща с мен, от което съм особено щастлив, че моето творчество е оценено.</p>\r\n<p>От 1979г. съм член на Съюза на българските композитори.</p>\r\n<p>Получил съм следните отличия до сега:</p>\r\n<ol>\r\n<li>Медал за участие през 1912-1913г.</li>\r\n<li>Медал за участие през 1915-1918г.</li>\r\n<li>Отечествената война 1944-1945г.</li>\r\n<li>За социалистическа родина</li>\r\n<li>За победу германий</li>\r\n<li>Юбилеен медал – \"25 години народна власт\"</li>\r\n<li>Юбилеен медал – \"1300 години България\"</li>\r\n<li>Юбилеен медал – \"100 години от рождението на Г.Димитров\"</li>\r\n<li>Медал \"За заслуги към БНА\"</li>\r\n<li>Орден \"Червено знаме на труда\"</li>\r\n<li>Орден \"Кирил и Методий\" – II степен</li>\r\n<li>Звание \"Заслужил деятел на културата\"</li>\r\n</ol>\r\n<p>Носител съм на:</p>\r\n<ul>\r\n<li>Почетен знак на Михайловград – златен</li>\r\n<li>Почетен знак на Враца</li>\r\n<li>Почетен знак \"Огоста\" на Окръжния съвет за култура – Михайловград</li>\r\n<li>Почетен знак на Враца – \"За прослава на родния Врачански край\"</li>\r\n<li>Почетен член съм на Клуба на културните дейци – Враца</li>\r\n</ul>\r\n<p>За моя живот и творчество са направени късометражни филми от София с режисьор Георги Аврамов – \"Блести златна тръба\" и \"Музика\". Последният се прожектира на 29.02.1973г. по Българската телевизия за първи път в 19,15ч. и във Враца на 80-годишния ми юбилей.</p>\r\n<p>От любителски киноклуб във Враца е изготвен филм за мен /около 10 минути/, където са заснети моменти от честването на юбилея ми през 1978г. във Враца.</p>\r\n<p>26.11.1982г. Подпис: Д.Илиев</p>\r\n<p>Михайловград</p>\r\n<p><b>PS</b>: Биографията на Дико Илиев е диктувана от самия него и записана от дъщеря му Александрия.</p>\r\n','','',''),(19,'bg','2013-03-29 11:42:34','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Стефан Савов','спомени','<img alt=\"\" style=\"float: right; margin: 0px 0px 10px 10px;\" src=\"/images/StefanSavov.png\" title=\"Стефан Савов\">\r\n<p>Аз, както и мнозина мои връстници, започнахме своята читалищна дейност още след завършване наг.мназиалното си образование. Беше време на война. По-възрастните от нас бяха вече войници, а ние, по-младите, чакахме реда си – всеки ден можеше да дойде съобщението в кой полк или в коя допълняюща дружина да се явим. Аз преживях почти цялаг.дина в такова очакване. Не работех почти нищо, но четях всичко, което ми попаднеше, останалото време отдавах на читалищната дейност.</p>\r\n<p>За мене, както и за моите връстници, тя се състоеше в това, да участваме и в разни вечеринки с някои номера – кой с каквото може, или пък да играем в някоя пиеса. Всичко това, естествено, ставаше от името на читалището и в негова полза. Парите от билетите събираше касиерът на читалището, а нам оставаше гордостта и радостта, че с участието си и ние сме дали своята лепта за поддържането му. Какво ли не измисляхме, какво ли не правехме, за да бъде вечерта интересна за публиката, та когато разбере, че пак ще участваме някъде, тя да остави работата иг.ижите си и да хукне към читалищния салон. А къде мислите, че беше този салон? Уви, такъв нямаше. Жителите на малкия тогава Фердинанд съвсем, изглежда, не чувстваха нужда от салон, затова го и нямаха, а за читалищен салон минаваше салонът за гимнастика в първоначалното училище до джамията, в който имаше малка сцена, която ние при всяко участие подмладявахме, като я украсявахме с нови кулиси – бели чаршафи или червена хартия, а понякога и с някоя тъкана домашна черга. Постоянна оставаше само завесата, рисувана някога си от самобитния художник Воин Стоичков.</p>\r\n<p>От всички любители, така се наричахме ние, тогавашните самодейци, най-много се проявявахме двамата – моят другар от юношески години Методи Тошев и аз. Имаше ли вечеринка, независимо от това, коя част от еснафа я даваше, ние почти винаги бивахме канени за участие. И не отказвахме, нали парите от входните билети отиваха в касата на читалището, това беше важното. И ние участвахме ту с дует от мандолина и китара / мандолината бях аз, а китарата Методи Тошев/, ту с диалог, комедиен, разбира се, в който много предпазливо се надсмивахме над някои наши съграждани, които ни гледаха от салона, ту като подготвители на вокални дамски дуети / серенадата на Шуберт, с участието на двете братовчедки Сийка Емануилова и Цеца Илиева /, ту с концертно рисуване.</p>\r\n<p>Веднъж нарисувах цар Фердинанд как стриже едно магаре и приказва на себе си/ надпис отдолу / : \"Вече няма какво да се стриже\", за което щяха да ме арестуват, ако не бяха се застъпили за мене някои граждани.</p>\r\n<p>Голям фурор произвеждаше винаги набързо организираният от няколко души оркестър, състоящ се от две мандолини, една китара, една цигулка и един инструмент, какъвто не е имало и няма да има никъде по света – \"магията\". Това си беше една чамова дъска с опънати на нея телове, които се удрят с пръчка, а същевременно дъската се удря в пода под такт, но така, че няколкото тенекиени колелца, закрепени на горния й край, отчаяно дрънкаха и даваха илюзия за зилове. Много смях падаше с тая \"магия\". Фурорът се дължеше на нея, защото ако останеше на другите инструменти със слаби майстори, публиката можеше и да си поспи.</p>\r\n<p>Свърши войната. Съюзът на немците, в който влизахме и ние, беше бит. В Русия работничеството начело с Ленин се бореше за съветска власт. У нас дойде още един погром. Всички ние, моите връстници, по-старите и по-младите от нас, които бяхме останали живи, се върнахме по домовете си и макар още телесно здрави, бяхме вече болни – чувствахме, че някой ни е ограбил, че нещо голямо, важно сме загубили в живота си - най-хубавите ниг.дини от младостта ни.</p>\r\n<p>Почти всички, и по-млади, и по-стари, се бяхме записали в Университета в София да учим право. И понеже малцина от нас имаха възможност да живеят в София, останахме си у дома и оттук изучавахме правото. Да, всички четяхме за изпити, и тъй като все пак ни оставаше свободно време, събирахме се и решавахме да го оползотворим в читалищната дейност.</p>\r\n<p>Една привечер ме срещна на булеварда Асен Греков / станал по-късно генерал/, той беше 3-4 години по –възрастен от мен, и ми каза: \"Стефчо, няколко души решихме да играем \"Отвъд\" на Антон Страшимиров. Каним те за суфльор, приемаш ли?\" \"Разбира се – отвърнах. Още тази вечер съм в училището\". И отидох. Любителите –самодейци бяха чели вече пиесата, разпределили бяха ролите и започнали да ги учат. Като разбрали, че без суфльор няма да го бъде, извикали ме. \"Отвъд\" е пиеса за живота на македонските българи. Асен играеше централната роля, а Аспарух, син на известния тогава прошенописец Рашко Грънчаров, играеше ролята на предателя. Сестра му Райна беше Павлина.</p>\r\n<p>Започнахме репетициите, разбира се, без режисьор, без постановчик. Тогава ние дори не знаехме, че за сцената трябвало и такива хора, специалисти. Знаеше се през коя врата се влиза и през коя се излиза и това беше достатъчно. Нареждаха се на сцената кой както му скимне. Суфлирайки им, аз разбрах, че трябва да се намеся, да им покажа кой къде да застане, кой как да произнесе известна фраза, на коя дума да сложи логическото ударение и пр. Всички ми се подчиняваха. Така, че аз бях не само суфльор, но и режисьор, без сам да зная истинското значение на тази специалност. По-късно, когато постъпих в Народния театър в София, я опознах добре и разбрах, че тя има най-разностранно значение, зависещо от обстановката, приятелството, от това, дали режисьорът е истински артист в театъра или само е временен гост в него и пр. и пр.</p>\r\n<p>Представлението мина с успех и, види се, това е дало подтик на тези, които го уреждаха, да се заловят с нова пиеса. А навярно затова, че аз се проявих като разбиращ по-добре от другите театралното изкуство, на мене бяха дали централната роля.</p>\r\n<p>Сега се бяха спрели на по-голяма пиеса, с повече действащи лица, защото, както разбрах сетне, много младежи искали да участват. Театрален свят!... Бяха се спрели на гоголевата комедия \"Ревизор\", а на мене бяха възложили ролята на градоначалника, която всъщност е първопричината да вляза след време в театъра и да отдам почти целия си живот нему.</p>\r\n<p>Започнахме репетициите. Втората главна роля, Хлестаков, играеше Методи Тошев, Ана Андреевна – Райна Игнатова, Мария Антоновна – Елена Първанова, Ляпкин-Тяцкин – Асен Греков, Земляника – Игнат Митов, Добчински –Ганчо Ганчев, търговеца Абдулин – Иван Михайлов, днес армейски генерал и член на ЦК на БКП, Осип – Емануил Михайлов, / брат на Иван Михайлов/ , началника на пощата – Методи Първанов, Свистунов – Мито Първанов и др. Но как аз щях да играя градоначалника, като бях тънък и слаб като щиглец, а околийският началник в града, в чиито дрехи трябваше да играя, беше извънредно пълен? На помощ дойдоха възглавниците от вкъщи: две от тях завързахме на гърдите и корема, една , по-малка – на задника. Така намъкнах широкия нов костюм на околийския началник. Най-трудният въпрос на постановката беше разрешен. Навярно сме играли добре, защото публиката доста ни ръкопляска, а гражданството изрази желание да да повторим пиесата. Това направихме след две седмици. Тогава станаха някои комични случки, за които дълго после сме си спомняли.</p>\r\n<p>Много комични случки е имало по време на моята любителска дейност, но те ставаха и отминаваха, без тях щеше да бъде скучно. Но си оставаше любовта ни към читалището, за което бяхме готови да жертваме всичко.</p>\r\n<p></p>\r\n<p><b>Н.а. Стефан Савов</b></p>\r\n','','',''),(20,'bg','2013-03-29 12:16:03','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Духов оркестър Дико Илиев','','<img alt=\"\" style=\"float: right; margin: 0px 0px 10px 10px;\" src=\"/images/do1.png\" title=\"ДО &quot;Дико Илиев&quot; - дефилира по улиците на София  по повод 110 г от рождението на Дико Илиев\">\r\n<p>Духов оркестър \"Дико Илиев\" при читалище \"Разум 1883\" – гр. Монтана носи с гордост името на големия самобитен композитор. През 2009 година отбеляза своя 80 годишен юбилей. Той е единственият читалищен колектив, който не е прекъсвал дейността си от своето създаване през 1929 година. Тогава група от 20 ентусиазирани младежи взели духови инструменти от Търговската гимназия и започнали да свирят, като в същото време се учели да разчитат ноти. Пръв диригент станал старият военен музикант Мустафа Алиев. По-късно читалището закупило 27 нови духови инструмента, ушило специална униформа и това привлякло вниманието на цялата общественост. Да имат своя духова музика, била мечтата на всички граждани на град Фердинанд и духовият оркестър бил посрещнат много радушно. Събитието било отбелязано във всички местни и регионални печатни издания по това време. До тогава на празници свирела военната духова музика в града.</p>\r\n<p>Много скоро съставът започва да изнася концерти на открито, да участва в читалищни вечеринки, да води шествия, да свири на празници и събития, да организира увеселителни вечери в града. Той станал много популярен и през 1931 година е поканен да свири на читалищния конгрес в гр София. Духовият оркестър израства много, когато за негов диригент е назначен Славейко Каменов – бивш военен музикант. За много кратко време обновява репертоара си, включва класически пиеси, български и чужди маршове, хора и забавна музика.</p>\r\n<img alt=\"\" style=\"float: left; margin: 0px 10px 10px 0px;\" src=\"/images/do2.png\" title=\"Концерт по повод 114 г от рождеинието на Дико Илиев\">\r\n<p>Не закъсняват и първите признания – първо място на окръжни прегледи през 1957 и 1958 година. През 1968г. на заключителния етап на Третия републикански фестивал на художествената самодейност, вече под диригенството на Рангел Тушев – дългогодишен военен диригент, духовият оркестър при читалище \"Разум\" печели второ място, сребърен медал и лауреатско звание. Същото признание получава и неговия диригент Рангел Тушев.</p>\r\n<p>След Фестивала за диригент на оркестъра е назначен Ненчо Тодоров - възпитаник на Българската държавна консерватория. Под неговото ръководство духовият оркестър постига високо изпълнителско майсторство и истинско професионално равнище. Оркестърът изцяло променя репертоара си – свирят се увертюри към опери и оперети, произведения на Петко Стайнов и други български композитори, над 100 марша, хора и шлагери. На ІV, V и VІ Републикански фестивали на художествената самодейност завоюва златни медали и лауреатско звание. Постепенно включва почти всички произведения на самобитния творец Дико Илиев, които стават неразделна част от бита на хората от Северозападна България. Самият композитор идва в Монтана вече на възраст, когато не може да държи диригентската палка, но посещава репетициите и концертите на състава, помага със съветите си и се ползва с уважението и обичта на оркестрантите. Последният концерт, който дирижира големият творец, е по повод 60 годишнината на духовия оркестър, когато почти сляп дирижира \"Дунавското хоро\", а публиката на крака аплодира цялото произведение - един незабравим, изключителво вълнуващ момент за монтанската общественост.</p>\r\n<p>За диригент на оркестъра през 1988 г е назначен Лило Лилов – дългогодишен преподавател в школата по изкуствата и оркестрант в състава. Той включва нови произведения от българската и световна класика в репертоара на оркестъра, забавна и народна музика, както и солисти – певци с разнообразна класическа музика и обработка на български народни песни. През 2002 година оркестърът участва в международен фестивал на духовите оркестри в град Шиклош – Унгария, а унгарското радио прави записи за музикалния си фонд - две хора на бай Дико и марша \"Сливенци при Драва\".</p>\r\n<img alt=\"\" style=\"float: right; margin: 0px 0px 10px 10px;\" src=\"/images/do3.png\" title=\"Великденски концерт 2012 г\">\r\n<p>Духовият оркестър има богата творческа биография, неизменно присъства в културния живот на града и общината и се ползва с обичта и подкрепата на монтанската публика. Традиционни са концертите му по повод рождената дата на Дико Илиев, увеселителни концерти в градската градина, по повод Нова Година, национални и местни празници и тържества, участва в литийни шествия, ритуали и др.</p>\r\n<p>През 2009г. оркестърът е поканен да вземе участие в националните тържества по повод 110 годишнината на Дико Илиев, организирани от Българското национално радио и в предаването \"Полет над нощта\" по Българскана национална телевизия.</p>\r\n<p>Златната равнина между Дунав и Ком, в чийто център е разположен град Монтана, е люлка на най-българските хора и маршове. В този край е роден и авторът на Дунавското хоро Дико Илиев. 10 години от живота си самобитният творец, чиито произвединя са станали емблема на националния ни дух, живее и работи в Монтана. След неговата смърт, духовият оркестър с гордост приема и носи до днес неговото име.</p>\r\n<p>От 2011 г ръководител на оркестъра е Пламен Димитров.</p>\r\n<p><strong>Отличия:</strong></p>\r\n<ul>\r\n<li>1958 г - Трети републикански фестивал на художествената самодейност:</li>\r\n<ul>\r\n<li>втора награда, сребърен медал и лауреатско звание за оркестъра</li>\r\n<li>втора награда, сребърен медал и лауреатско звание за диригента Рангел Тушев</li>\r\n</ul>\r\n<li>1963 г - Четвърти републикански фестивал на художествената самодейност</li>\r\n<ul><li>Златен медал и лауреатско звание за духовия оркестър и дирегента Славейко Каменов</li></ul>\r\n<li>1968 г – Пети републикански фестивал на художествената самодейност</li>\r\n<ul><li>Златен медал и лауреатско звание за духовия оркестър, златен медал за диригента Ненчо Тодоров</li></ul>\r\n<li>1973 г - Шести републикански фестивал на художествената самодейност</li>\r\n<ul><li>Златен медал и лауреатско звание за духовия оркестър, златен медал за диригента Ненчо Тодоров</li></ul>\r\n<li>2002 г - Грамота и запис за Унгарското национално радио в рамките на Международен фестивал на духовите оркестри гр. Шиклош, Унгария</li>\r\n<li>2005 г - Почетен знак на община Монтана, по повод 75 години от създаването на оркестъра</li>\r\n<ul>\r\n<li>Златна значка на община Монтана за диригента Лило Лилов</li>\r\n<li>Награда от Съюза на музикалните и танцови дейци</li>\r\n</ul>\r\n<li>2009г. – Участие в тържества по повод 110 години от рождението на Дико Илиев, организирани от БНР - София.</li>\r\n<li>Грамота и статуетка от всички издания на \"Празниците на духовите оркестри \'Дико Илиев\'\" – Монтана - 1986, 1988, 1990, 1992, 1994, 1996, 1998, 2010, 2002, 2004, 2006, 2008, 2010г.</li>\r\n</ul>\r\n','','',''),(21,'bg','2013-03-29 14:01:42','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Фолклорен танцов ансамбъл Пъстрина','','<img alt=\"\" style=\"float: right; margin: 0px 0px 10px 10px;\" src=\"/images/ta1.png\" title=\"ФТА &quot;Пъстрина&quot; - Детски танцов състав\">\r\n<p>Ансамбълът е създаден през 1981 година в град Монтана. Главен художествен ръководител и хореограф на състава - Първан Първанов.</p>\r\n<p>От 2005 година към него функционира и детска танцова школа, в която участват 35 деца на възраст от 7-11 години. Детският състав има своите първи награди и отличия от прегледи и фестивали.</p>\r\n<ul>\r\n<li>2007г. – ІІІ място на Международен детски фестивал \"Лачени обувки\" – гр Берковица</li>\r\n<li>2009г. – участие в МФФ – гр Приморско, награден с купа на Община Приморско и златен медал</li>\r\n<li>2012г. – І място на ФФ \"Напеви от Северозапада\" – гр Монтана</li>\r\n<li>2012г. - участие в МФФ – Приморско – плакет на фестивала и златен медал</li>\r\n<li>2012г. - участие в национален детски фестивал – София – ІІ награда.</li>\r\n</ul>\r\n<p>Високата хореографска подготовка позволява на изпълнителите да включат в своя репертоар танци от всички етнографски райони на България. Танците са смесени, еднородни и камерни. В танцовия ансамбъл участват 40 танцьори и оркестър от 5-има музиканти. Добре подбраната програма с успех е показвана в България и чужбина. Тя може да се изпълнява както от целия ансамбъл, така и от камерна формация.</p>\r\n<p>Ансамбъл \"Пъстрина\" е участвал и печелил награди на фестивали в Канада, Холандия, Унгария, Сърбия, Македония, Гърция, Словакия, Турция, Румъния, Полша, Германия, Белгия, Испания, Франция и Португалия.</p>\r\n<p>Работи към читалище \"Разум\" от 2001 година.</p>\r\n<p><b>Награди:</b></p>\r\n<dl>\r\n	<dt>1983г.</dt><dd>Златен медал от VI I РФХС</dd>\r\n	<dt>1989г.</dt><dd>Златен медал от VII РФХС</dd>\r\n</dl>\r\n<p><b>Участие във Международни фолклорни фестивали:</b></p>\r\n<dl>\r\n<dt>1988г.</dt>\r\n	<dd>гр. Дакс - Франция</dd>\r\n<dt>1990г.</dt>\r\n	<dd>гр. Катерини – Гърция</dd>\r\n<dt>1992г.</dt>\r\n	<dd>гр. Амиевил – Франция – присъдена първа награда</dd>\r\n<dt>1993г.</dt>\r\n	<dd>гр. Лимож – Франция и Амиевил – Франция</dd>\r\n<dt>1994г.</dt>\r\n	<dd>гр. Хеез – Холандия и гр. Беркхайк – Холандия</dd>\r\n<dt>1995г.</dt>\r\n	<dd>гр. Монтана - България</dd>\r\n<dt>1996г.</dt>\r\n	<dd>гр. Абанила – Испания, гр. Родес – Франция, гр. Сансер – Франция</dd>\r\n<dt>1997г.</dt>\r\n	<dd>гр. Анкара – Турция</dd>\r\n<dt>1998г.</dt>\r\n	<dd>гр. Суза – Италия – първа награда и купа на кметството</dd>\r\n<dt>1999г.</dt>\r\n	гр. Бургас – България\r\n<dt>2000г.</dt>\r\n	<dd>гр.Агридженто - Италия – награда на публиката</dd>\r\n	<dd>гр. Вестерло- Белгия и гр. Ароука – Португалия</dd>\r\n<dt>2001г.</dt>\r\n	<dd>20 годишнина на ансамбъла, \"Сребърна лира\" на Съюза на музикалните и танцови дейци в България; Почетна значка на община Монтана.</dd>\r\n<dt>2002г.</dt>\r\n	<dd>гр. Раника, Италия</dd>\r\n	<dd>гр. Дакс, Франция</dd>\r\n	<dd>участие в празниците в гр. Шмалкалден, Германия</dd>\r\n<dt>2003г.</dt>\r\n	<dd>концертно турне в градовете Имола, Руси и Авиано,Италия</dd>\r\n	<dd>присъдено звание \"Представителен на ЦИОФФ за България\" от Първия преглед на фолклорните ансамбли на организацията</dd>\r\n<dt>2004г.</dt>\r\n	<dd>Танцов фестивал -гр. Тревизо, Италия</dd>\r\n<dt>2005г.</dt>\r\n	<dd>Танцов фестивал гр. Матосинош /Порто/, Португалия, първо място</dd>\r\n	<dd>концерт в гр. Пирот и гр. Белград, Сърбия</dd>\r\n<dt>2006г.</dt>\r\n	<dd>Танцов фестивал - Паняко и Сапада, Италия, о-в Сардиния, Италия</dd>\r\n	<dd>концерт в гр. Пирот, Сърбия - печели голямата награда</dd>\r\n	<dd>честване 25 години ансамбъл \"Пъстрина\"</dd>\r\n	<dd>участие в концерт в гр. Каракал, Румъния</dd>\r\n<dt>2007г.</dt>\r\n	<dd>Танцови фестивали в гр. Тулон, Франция</dd>\r\n	<dd>Абрагао-Пенафиел,Португалия</dd>\r\n	<dd>концерт в гр. Пирот, Сърбия</dd>\r\n	<dd>присъдено звание \"Представителен на ЦИОФФ за България\" от II –ия преглед на фолклорните ансамбли на организацията</dd>\r\n<dt>2008г.</dt>\r\n	<dd>участие в \"Mondial des Kultures de Drumondville\", Канада</dd>\r\n	<dd>гр. Суза, Италия – печели купата на фестивала</dd>\r\n	<dd>Концерт в гр. Бъйлещи,Крайова</dd>\r\n<dt>2009г.</dt>\r\n	<dd>концертно турне в гр. Каракал, Румъния</dd>\r\n	<dd>участие в МФФ в Реджо ди Калабрия, Италия</dd>\r\n<dt>2010г.</dt>\r\n	<dd>участие в концерт на празника \"Калуша\" в гр. Каракал, Румъния</dd>\r\n	<dd>гр. Познан, Полша, присъдено звание \"Представителен на ЦИОФФ за България\" от III-ия преглед на фолклорните ансамбли на организацията</dd>\r\n<dt>2011г.</dt>\r\n	<dd>Участие на ТА \"Пъстрина\" в Международен фестивал – гр Ниш, Сърбия</dd>\r\n<dt>2012г.</dt>\r\n	<dd>Участие на ТА\"Пъстрина\" в Международен фестивал - Реджо ди Калабрия, Италия</dd>\r\n</dl>\r\n<img alt=\"\" style=\"float: left; margin: 0px 10px 10px 0px;\" src=\"/images/ta2.png\" title=\"ТА &quot;Пъстрина&quot;- концерт по повод 125 г НЧ &quot;Разум&quot;\">\r\n<img alt=\"\" style=\"float: left; margin: 0px 10px 10px 0px;\" src=\"/images/ta3.png\" title=\"ТА &quot;Пъстрина&quot; на Международен танцов фестивал - Канада\">\r\n','','',''),(22,'bg','2013-03-29 14:21:47','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Мъжки хор','','<img alt=\"\" style=\"float: right; margin: 0px 0px 10px 10px;\" src=\"/images/hor1.png\" title=\"Концерт в Музея &quot;Земята и хората&quot; – София - 2011г.\">\r\n<p>Мъжкият хор е създаден през 2000г. Включва 16 изпълнители. Диригент на състава е Снежина Георгиева. От 2008 година главен художествен ръкъводител e Марио Пенев.</p>\r\n<p>През първите си седем творчески години хорът е моделиран от доцент Чавдар Маждраков. В репертоара на състава са включени произведения на българската хорова класика, църковнославянски песнопения, песни от европейската и световна класика, руски народни песни и романси. Съставът е носител на наши и международни награди – Лауреат и носител на сребърен медал от фестивала \"Бохослав Мартину\" – Чехия, награда на публиката в хоров фестивал в град Банска Бистрица – Словакия и др. Активно участва в културния живот на град Монтана, като запазва и продължава най-добрите хорови традиции в града.</p>\r\n<p><b>Отличия:</b></p>\r\n<p>2002г. – Награда на журито от 24 фестивал на хоровата песеен гр. Банска</p>\r\n<p>Бистрица – Словакия</p>\r\n<p>2003г. – Сребърен диплом от Третия международен хоров конкурс гр.Пардубице – Чехия</p>\r\n<p>2004г. – Концертно турне гр. Фрайбург, Германия</p>\r\n<p>2006г. – Концертно турне гр. Дрезден, Германия – участие в тържествата по повод 800-годишнината от основаването на града</p>\r\n<p>2007г. – Участие в Международния хоров фестивал гр. Храдец Кралове, Чехия</p>\r\n<p>2008г. – Концерт в Европейския парламент, Брюксел</p>\r\n<p>2008г. – Участие в Панорама на камерните хорове „Никола Липов” гр. Пловдив</p>\r\n<p>2009г. - Златни медали в категориите „фолклор” и „класика” на Международния конкурс за еднородни камерни хорове \"Юнски звуци\" гр. Петринка, Хърватска</p>\r\n<p>2010г. – Бронзов медал от 16-ия международен хоров конкурс гр. Превеза, Гърция</p>\r\n<p>2011г. - Концерт на мъжки хор по повод 100г от създаването на СНЧ –НДК София</p>\r\n<p>2012г. - Участие на мъжки хор в Международен фестивал – гр Краков, Полша – ІІ награда</p>\r\n<img alt=\"\" style=\"float: left; margin: 0px 10px 10px 0px;\" src=\"/images/hor2.png\" title=\"Концерт в катедрала в гр Дрезден, Германия\">\r\n<img alt=\"\" style=\"float: left; margin: 0px 10px 10px 0px;\" src=\"/images/hor3.png\" title=\"Концерт в катедрала в гр Дрезден, Германия\">\r\n','','',''),(23,'bg','2013-03-29 14:32:48','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Група за стари градски песни и шлагери Носталгия','','<img alt=\"\" style=\"float: right; margin: 0px 0px 10px 10px;\" src=\"/images/nostalgia1.png\" title=\"Концерт в художествена галерия &quot;Кирил Петров&quot;\">\r\n<p>Създадена през 1988г. като наследник на читалищната оперета. Първи участници: Павлина Папуркова, Тереза Лилова, Радка Аврамова и Росица Николова. Първият ръководител е Петя Кисимова, тя акомпанира и на пиано. От 1995 до 2007 г. ръководител на групата е Николай Дамянов. Тогава с групата работи малък ретро оркестър за забавна музика - Елка Данаилова -контрабас, Иво Върбанов – акордеон, Цецко Митов – кларинет. В този период съставът изпълнява популярни стари градски песни, музикални пиеси от български и чуждестранни филми, оперетни миниатюри от стари наши оперети. Реализира няколко самостоятелни естрадно-оперетни спектакли, два от които отразени в специалните предавания на БНТ в предаването \"Минаха години\". С репертоара, със стила, с костюмите си, група \"Носталгия\" печели почитатели, конкурси и фестивали.</p>\r\n<p><b>Награди</b></p>\r\n<p>1994г. - Второ място за изпълнение на фестивала \"Златен кестен\" – гр. Петрич.</p>\r\n<p>1995г. - Първо място на фестивала \"Дионисополис\" – Балчик.</p>\r\n<p>1995г. - Първо място за изпълнение на фестивала \"Златен кестен\" в Петрич.</p>\r\n<p>1996г - Специална награда за изпълнение на песен с морска тематика на Международния фестивал \"Море и спомени\" – Варна ,96 година.</p>\r\n<p>2006г.- Втора награда за изпълнение на фестивал \"Мара Врачанка\"- гр. Враца.</p>\r\n<p>2007г. - Награда за най-стилни костюми на фестивал \"Мара Врачанка\" – Враца.</p>\r\n<p>2007г. - Концерт на групата в град Гостивар – Македония.</p>\r\n<p>От 2008 г. ръководител на групата е Атанас Проданов. В репертоара си има популярни градски песни от 40 – 60 -те години на миналия век.</p>\r\n<p>2010 г. - Награда за най-стилен костюм на фестивал \"Мара Врачанка\" – гр. Враца.</p>\r\n<img alt=\"\" style=\"float: left; margin: 0px 10px 10px 0px;\" src=\"/images/nostalgia2.png\" title=\"Празници на старата градска песен – Монтана, 2009\">\r\n<img alt=\"\" style=\"float: left; margin: 0px 10px 10px 0px;\" src=\"/images/nostalgia3.png\" title=\"Концерт в художествена галерия &quot;Кирил Петров&quot;\">\r\n','','',''),(24,'bg','2013-03-29 14:39:58','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Фолклорна формация Огоста','','<img alt=\"\" style=\"float: right; margin: 0px 0px 10px 10px;\" src=\"/images/ogosta1.png\" title=\"Фолклорна формация &quot;Огоста&quot;\">\r\n<p>Най-новият състав в читалището, създаден през 2011г., продължител на фолклорните традиции, развивани в читалището от години. Ръководител – Цветан Цветанов, основател и диригент на ФФ \"Турлашки напеви\", който си поставя задачата да издирва и пресъздава на сцената красотата на фолклора и песните от Северозападна България. Кръстница на състава е народната певица Соня Чакърова. Съставът завоюва първа награда на Международния фолкларен фестивал в гр. Кюстендил 2012г., има успешни концертни изяви в града и Област Монтана.</p>\r\n<img alt=\"\" style=\"float: left; margin: 0px 10px 10px 0px;\" src=\"/images/ogosta2.png\" title=\"Фолклорна формация &quot;Огоста&quot;\">\r\n<img alt=\"\" style=\"float: left; margin: 0px 10px 10px 0px;\" src=\"/images/ogosta3.png\" title=\"Фолклорна формация &quot;Огоста&quot;\">\r\n','','',''),(25,'bg','2013-03-29 14:52:18','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Оперета','','<img alt=\"\" style=\"float: right; margin: 0px 0px 10px 10px;\" src=\"/images/opereta1.png\" title=\"\">\r\n<p><b>През 1922 година учителят по музика Иван Бърдаров</b>, заедно с ученици от Търговската гимназия и симфоничния оркестър в читалището, поставят в град Фердинанд оперетата \"Самодивското изворче\" от Георги Атанасов. Тогава не съществува днешната сграда на читалището и представлението става в ремонтирания турски яхър. Градът наброява 5000 жители, 40% от който са неграмотни. Постановката е много успешна, приема се от гражданството с интерес и след 2 седмици спектакълът се повтаря отново. В следващите години се правят и други опити / \"Балът на цветята\",/ да се поставят \"оперетки\" от Георги Атанасов, както той самичък ги нарича, с простичка и наивна драматургия, но мелодични и с приятна музика.</p>\r\n<p>За периода 1945 – 1947 г се поставят нови оперетни постановки - \"Бойчо\", \"Сашка\", \"Запорожци зад Дунава\" и др. Диригент на хора, участник в оперетните постановки и изпълнител на главните роли е Самуел Видас - интерниран от град София, а диригент на симфоничния оркестър е Кирил Кожухаров. Освен оперетни спектакли, двата състава организират заедно концерти по различни поводи в града и близките села.</p>\r\n<p>През 1956 г се играе оперетата \"Майска нощ\", но с мюзикъла \"Ревю на младостта\" - 1959 г, оперетният колектив поставя началото на един продължителен и много плодотворен период, в който на читалищна сцена се играят най-известните класически оперети – \"Целувката на Чанита\", \"Прилепът\", \"Бедният студент\", \"Царицата на чардаша\", \"Българи от старо време\", \"Веселата вдовица\" и мн др. Спектаклите се играят над 30 пъти на сезон, гостуват в по-големите села на окръга.</p>\r\n<img alt=\"\" style=\"float: left; margin: 0px 10px 10px 0px;\" src=\"/images/opereta2.png\" title=\"\">\r\n<p>Тези постановки печелят най-високите републикански награди, а гражданството живее с проблемите и успехите на оперетния колектив и след всеки фестивал посреща участниците като победители. В същото време оперетният колектив се явява на Републиканските фестивали на художествената самодейност и завоюва златни медали и лауреатско звание. За отличното представяне на оперетата \"Бедният студент\" на Третия РФХС през 1969 г, оперетният колектив получава 13 златни и 3 сребърни медала, с което се нарежда на първо място в страната в националните прегледи. Колективът става най-популярния и обичан самодеен състав, солистите му се ползват с уважението и симпатията на цялата общественост.</p>\r\n<p>Най-активни и с огромен принос към оперетния колектив са самодейците Павлина Папуркова, Никола Петков, Тереза Лилова, Цено Беляшки, Венета Гоголева, Ангел Рангелов, Александър Василев, Атанас Ташков и др, диригента Димитър Найденов, вокалния педагаг Руско Русков, режисьорите Стефан Иванов, Емил Пенчев и много други изпълнители от солистите, хора, оркестъра и балета. Поради липса на сцена и салон, оперетния колектив преустановява дейността си през 90-те години на миналия век. Диригенти на оперетния колектив са били Петър Ганев, Касабов, Петя Кисимова.</p>\r\n<img alt=\"\" style=\"float: right; margin: 0px 0px 10px 10px;\" src=\"/images/opereta3.png\" title=\"\">\r\n','','',''),(26,'bg','2013-03-29 15:44:01','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Обединена школа по изкуствата Добри Христов',NULL,NULL,NULL,NULL,NULL),(27,'bg','2013-03-29 15:57:03','0000-00-00 00:00:00',1,1,7,NULL,NULL,'История',NULL,NULL,NULL,NULL,NULL),(28,'bg','2013-03-29 15:57:28','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Изяви',NULL,NULL,NULL,NULL,NULL),(29,'bg','2013-03-29 16:23:59','2013-05-08 15:24:38',1,1,7,NULL,NULL,'История','','<p>Народно читалище \"Разум\" – гр. Монтана е създадено през 1883 г. Първият известен председател е Гаврил Душков, преселен учител от Копривщица, който донася от китния град възрожденските идеи на времето.</p>\r\n<p>Като всички създадени читалища в този период, читалище \"Разум\" поставя началото на библиотечната, театралната и музикална дейност в нашия град. Организират се вечеринки, едноактни пиеси, четения, декламации и др. Същевременно се създава въздържателно дружество, туристическо, граждански хор, оркестър и др. Изнасят се публични сказки, на които се гледа като на много важно средство за разширяване знанията на хората.</p>\r\n<p>През 1911 г. читалището закупува парцел в центъра на града. Този парцел принадлежал преди Освобождението на турската управа и ремонтираният конак станал първата читалищна сграда. По-късно, от 1934 до 1937 г, с помоща на населението от нашия град, се построява голяма сграда, която читалището ползва до днес.</p>\r\n<p>До 70-те години на миналия век, читалище \"Разум\" е единственият културен институт в нашия град, който съчетава в себе си цялата културна и просветителска дейност. През 1922 г тук се прави първата оперетна постановка \"Самодивското изворче\", през 1929 г - духов оркестър, който функционира и до днес, симфоничен оркестър /1926 г/, който заедно с гражданския хор развива активна културна дейност в цяла Северозападна България. Оркестърът озвучава немите филми, изнася много концерти и става основа на създадения по-късно оперетен състав. Особено активна дейност има и театралния колектив, който създава доста известни имена, най-известното от които е това на н.а. Стефан Савов.</p>\r\n<p>В читалище \"Разум 1883\" се създават градското кино, драматичния театър, музикалната и балетна школа, кукления театър.</p>\r\n<p>През годините на своето развитие читалището е организирало над 40 вида дейности. Неговите колективи са носители на престижни международни, национални и местни награди, лауреати и носители на златни медали от Републиканските фестивали на художествената самодейност.</p>\r\n<p>Ползвайки се с авторитета на най-стария културен институт, днес читалище \"Разум 1883\" активно участва в културния живот на нашия град, а художествените състави с чест защитават достиженията на българския фолклор по европейските сцени. В неговите форми на работа се занимават над 250 самодейци - Духов оркестър \"Дико Илиев\", Танцов ансамбъл \"Пъстрина\", народен хор, мъжка вокална група, Група за стари песни и шлагери \"Носталгия\" и Школа по изкуствата \"Добри Христов\". Тези състави водят активен културен живот, участват във фестивали и прегледи, всяко лято организират турнета в чужбина, носители са на много републикански и международни награди.</p>\r\n<p>В последните години читалище \"Разум 1883\" избра новия поглед към читалищната дейност – предизвикателството да участва в процеса на гражданското приобщаване, да направи дейността си по-социална и по-ангажирана. Това му донесе признание и успех – спечелени проекти - \"Център за правенция на наркомании\", \"Изкуство срещу дрогата\" с високата оценка от Нийл Боне –представител на ООН за България, \"Фолклорна палитра\" – проект, който обединява културата на различните социални общности, Проект \"Ден на Европа\", работа с японски доброволец и др.</p>\r\n<p>В Школата по изкуствата се обучават над 150 деца в три направления – музика, танцово, изобразително изкуство и актьорско майсторство. Школата активно присъства в обществения и културен живот на гр Монтана с концерти, спектакли, пленери, изложби, класови продукции и др. Има завоювани много награди – национални от различни фестивали и прегледи и международни от Индия, Македония, Полша, Словения, Япония, Португалия и др.</p>\r\n<p>Читалище \"Разум 1883\" се ползва с подкрепата и уважението на монтанската общественост. За 127 години хиляди хора са се включвали в различните форми на читалищната дейност и са оставили в него част от своя талант и дарование и това е голямата историческа заслуга на читалището. Основна негова задача винаги е била съхраняването на българските традиции и българския дух и големия, самобитен талант на нашия народ.</p>\r\n\r\n','','',''),(30,'bg','2013-03-29 16:29:24','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Танцов състав',NULL,NULL,NULL,NULL,NULL),(31,'bg','2013-03-29 16:49:00','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Литературно студио',NULL,NULL,NULL,NULL,NULL),(32,'bg','2013-03-29 16:50:14','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Фото клуб',NULL,NULL,NULL,NULL,NULL),(33,'bg','2013-04-03 13:47:15','0000-00-00 00:00:00',1,1,2,NULL,NULL,'Концерт на Група \"Носталгия\"','Страстно обичам жените','<p>Обновената Група за стари песни и шлагери \"Носталгия\" при читалище \"Разум 1883\" – гр Монтана ще  представи  своя пролетен концерт на 26.ІІІ.2013 г в салона на младежкия дом от 18 часа. Концертът е посветен на Павлина Папуркова – дългогодишна самодейка, примадона на читалищната оперета и основател на група \"Носталгия\", която читалището загуби преди няколко седмици.</p>\r\n',NULL,NULL,NULL),(34,'bg','2013-04-03 14:01:39','0000-00-00 00:00:00',1,1,2,NULL,NULL,'Годишно отчетно събрание',NULL,'<p>На 4 май 2013 г от 18 часа  ще  се проведе  годишното отчетно събрание на читалището в малкия салон. Новоизбраното Настоятелство ще отчете дейността си за 2012 г, ще бъде приет финансов отчет и програма за работата на читалището през 2013 г.</p>\r\n',NULL,NULL,NULL),(35,'bg','2013-04-03 14:21:56','0000-00-00 00:00:00',1,1,2,NULL,NULL,'Спектакли на децата от класа по куклено-актьорско майсторство',NULL,'<p>Много амбициозна програма са си поставили децата от Школата по изкуствата – класа по куклено-актьорско майсторство с ръководител Наташа Крумова.</p>\r\n<p>Ще бъдат организирани 14 спектакли на куклените пиеси \"Житената питка\" и \"Клан, клан -недоклан\" съвместно с Общинската библиотека \"Гео Милев\" за децата от всички училища в града до края на м април.</p>\r\n<p>Малките актьори се обучават само от 5 месеца, но съвсем наскоро се радваха на голям успех на премиерите на двете пиески.</p>\r\n',NULL,NULL,NULL),(36,'bg','2013-04-03 14:25:41','0000-00-00 00:00:00',1,1,2,NULL,NULL,'130г НЧ \"Разум 1883\"',NULL,'<p>Тази година се навършват  130 г от основаването на НЧ \"Разум 1883\" – гр Монтана.</p>\r\n<p>Най-старият културен институт в град  Монтана – НЧ \"Разум 1883\" се готви  да отбележи своята\r\n130 годишнина. По този повод е подготвена  разнообразна програма, която включва\r\n самостоятелни концерти на всички колективи, изложби, гостувания и др.</p>\r\n<p>Тържествата ще се открият на 8.V.2013 г  пред сградата на читалището  и ще завършат  с  гала- \r\n концерт на 17.V.2013 г в Драматичния театър.</p>\r\n<p>В концерта  ще вземат участие настоящи и бивши самодейци и възпитаници на Школата по \r\n изкуствата , които  днес работят в престижни културни институции.</p>\r\n<p>Ще бъдат експонирани две изложби -  документална фото-изложба и изложба на  детски рисунки.\r\n Със самостоятелен концерт от настоящи и бивши възпитаници ще  се представи и ОШИ \"Д. Христов\".\r\n</p>\r\n',NULL,NULL,NULL),(37,'bg','2013-04-08 14:58:25','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Духов оркестър Дико Илиев',NULL,NULL,NULL,NULL,NULL),(38,'bg','2013-04-08 16:27:03','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Фолклорен танцов ансамбъл Пъстрина',NULL,NULL,NULL,NULL,NULL),(39,'bg','2013-04-08 16:47:29','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Мъжки хор',NULL,NULL,NULL,NULL,NULL),(40,'bg','2013-04-08 16:54:03','0000-00-00 00:00:00',1,1,7,NULL,NULL,'Група за стари градски песни и шлагери Носталгия',NULL,NULL,NULL,NULL,NULL),(82,'en','2013-05-10 14:39:11','2013-05-10 15:13:24',1,1,7,NULL,NULL,'Народно читалище “Разум 1883”','','<p>Народно читалище “Разум 1883”<br></p><br>','','','');

